import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { TaskList } from "./components/TaskList";

/**
 * Custom Todo App Main Component
 * - Renders the TaskList as the main page
 * - No authentication required for solo use
 * - All boilerplate/auth screens removed
 */
function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<TaskList />} />
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
</action>
