import { create } from 'zustand'
import { persist } from 'zustand/middleware'
import axios from 'axios'

const API_URL = import.meta.env.VITE_API_URL;

export interface User {
  id: number;
  email: string;
  is_active: boolean;
  created_at: string;
  updated_at?: string;
}

interface AuthState {
  user: User | null;
  isAuthenticated: () => boolean;
  accessToken: string | null;
  login: (email: string, password: string) => Promise<void>;
  register: (email: string, password: string) => Promise<void>;
  logout: () => void;
}

export const useAuthStore = create<AuthState>()(
  persist(
    (set, get) => ({
      user: null,
      accessToken: null,
      isAuthenticated: () => {
        const token = get().accessToken;
        return !!token; // Check if token exists
      },
      login: async (email, password) => {
        try {
          const response = await axios.post(`${API_URL}/auth/token/`, new URLSearchParams({
            username: email,
            password: password,
          }), {
            headers: {
              'Content-Type': 'application/x-www-form-urlencoded',
            },
          });
          const { access_token } = response.data;
          localStorage.setItem('accessToken', access_token);

          // Fetch user details after successful login
          const userResponse = await axios.get(`${API_URL}/auth/users/me/`, {
            headers: {
              Authorization: `Bearer ${access_token}`,
            },
          });
          const user: User = userResponse.data;

          set({ user, accessToken: access_token });
          console.log('Login successful:', user);
        } catch (error) {
          console.error('Login failed:', error);
          localStorage.removeItem('accessToken');
          set({ user: null, accessToken: null });
          throw error; // Re-throw to be caught by UI components
        }
      },
      register: async (email, password) => {
        try {
          const response = await axios.post(`${API_URL}/auth/register/`, { email, password });
          const user: User = response.data;
          set({ user }); // User is registered, but not logged in automatically
          console.log('Registration successful:', user);
        } catch (error) {
          console.error('Registration failed:', error);
          throw error; // Re-throw to be caught by UI components
        }
      },
      logout: () => {
        localStorage.removeItem('accessToken');
        set({ user: null, accessToken: null });
        console.log('Logged out');
      },
    }),
    {
      name: 'auth-storage',
      getStorage: () => localStorage,
      onRehydrateStorage: (state) => {
        // This runs when the store is rehydrated from storage
        const token = localStorage.getItem('accessToken');
        if (token) {
          state.accessToken = token;
          // Optionally, fetch user details if token is valid
          // This would require an async operation here, which `onRehydrateStorage` doesn't directly support for `set`
          // A common pattern is to have an `initializeAuth` action that runs on app load
        }
      },
      // Only persist accessToken, user will be fetched on login or rehydrated
      partialize: (state) => ({ accessToken: state.accessToken }),
    }
  )
);

// Function to initialize auth state on app load
export const initializeAuth = async () => {
  const { accessToken, isAuthenticated, login, logout, user } = useAuthStore.getState();
  if (accessToken && !user) {
    try {
      // Attempt to fetch user details with the stored token
      const userResponse = await axios.get(`${API_URL}/auth/users/me/`, {
        headers: {
          Authorization: `Bearer ${accessToken}`,
        },
      });
      useAuthStore.setState({ user: userResponse.data });
      console.log('Auth rehydrated and user fetched:', userResponse.data);
    } catch (error) {
      console.error('Failed to re-authenticate user with stored token:', error);
      logout(); // Clear invalid token
    }
  }
};

// Call initializeAuth when the module is loaded
initializeAuth();
