import os
import requests

OPENWEATHERMAP_URL = "https://api.openweathermap.org/data/2.5/weather"

def get_weather(city: str, use_mock: bool = False):
    if use_mock or os.getenv("USE_MOCK", "false").lower() == "true":
        # Deterministic mock response
        return {
            "city": city,
            "weather": "Clear",
            "temperature": 22,
            "description": "Mocked clear sky",
            "mock": True
        }
    api_key = get_openweathermap_key()
    if not api_key:
        return {"error": "No OpenWeatherMap API key configured."}
    params = {"q": city, "appid": api_key, "units": "metric"}
    try:
        resp = requests.get(OPENWEATHERMAP_URL, params=params, timeout=5)
        data = resp.json()
        if resp.status_code != 200:
            return {"error": data.get("message", "Failed to fetch weather")}
        return {
            "city": city,
            "weather": data["weather"][0]["main"],
            "temperature": data["main"]["temp"],
            "description": data["weather"][0]["description"],
            "mock": False
        }
    except Exception as e:
        return {"error": str(e)}

def get_openweathermap_key():
    # Try env first, then file
    key = os.getenv("OPENWEATHERMAP_API_KEY")
    if key:
        return key
    # Fallback: read from file
    try:
        with open("backend/.weather_api_key", "r") as f:
            return f.read().strip()
    except Exception:
        return None
