from fastapi import APIRouter, Body, HTTPException
from services.credentials_service import save_key, get_key, delete_key, get_masked_keys

router = APIRouter()

@router.post("/settings/keys")
def add_key(data: dict = Body(...)):
    service = data.get("service")
    key = data.get("key")
    if not service or not key:
        raise HTTPException(status_code=400, detail="Missing service or key")
    save_key(service, key)
    return {"status": "success"}

@router.get("/settings/keys")
def list_keys():
    return get_masked_keys()

@router.delete("/settings/keys/{service}")
def remove_key(service: str):
    delete_key(service)
    return {"status": "deleted"}
