import os
import json

CREDENTIALS_FILE = "backend/.credentials.json"

def ensure_dir_exists():
    dir_path = os.path.dirname(CREDENTIALS_FILE)
    if dir_path and not os.path.exists(dir_path):
        os.makedirs(dir_path, exist_ok=True)

def save_key(service: str, key: str):
    ensure_dir_exists()
    creds = load_all_keys()
    creds[service] = key
    with open(CREDENTIALS_FILE, "w") as f:
        json.dump(creds, f)

def get_key(service: str):
    creds = load_all_keys()
    return creds.get(service)

def delete_key(service: str):
    ensure_dir_exists()
    creds = load_all_keys()
    if service in creds:
        del creds[service]
        with open(CREDENTIALS_FILE, "w") as f:
            json.dump(creds, f)

def load_all_keys():
    ensure_dir_exists()
    if not os.path.exists(CREDENTIALS_FILE):
        return {}
    try:
        with open(CREDENTIALS_FILE, "r") as f:
            return json.load(f)
    except Exception:
        return {}

def get_masked_keys():
    creds = load_all_keys()
    masked = {}
    for service, key in creds.items():
        masked[service] = mask_key(key)
    return masked

def mask_key(key: str):
    if len(key) <= 4:
        return "*" * len(key)
    return "*" * (len(key) - 4) + key[-4:]
