import {
  Box,
  Container,
  Heading,
  Text,
  VStack,
  Input,
  Button,
  Spinner,
  Alert,
  AlertIcon,
  Card,
  CardBody,
  useColorModeValue,
  HStack,
  IconButton,
} from '@chakra-ui/react'
import { useState } from 'react'
import { FiSettings, FiCloud } from 'react-icons/fi'

const BACKEND_URL = import.meta.env.VITE_BACKEND_URL || "http://localhost:8001"

export default function HomePage() {
  const [city, setCity] = useState("")
  const [weather, setWeather] = useState<any>(null)
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")
  const [history, setHistory] = useState<string[]>([])

  const cardBg = useColorModeValue('white', 'gray.800')

  const fetchWeather = async () => {
    setLoading(true)
    setError("")
    setWeather(null)
    try {
      const resp = await fetch(`${BACKEND_URL}/weather?city=${encodeURIComponent(city)}&use_mock=false`)
      const data = await resp.json()
      if (data.error) {
        setError(data.error)
      } else {
        setWeather(data)
        setHistory(h => [city, ...h.filter(c => c !== city)].slice(0, 5))
      }
    } catch (e) {
      setError("Network error. Please try again.")
    }
    setLoading(false)
  }

  return (
    <Box minH="100vh" bg={useColorModeValue('gray.50', 'gray.900')}>
      <Container maxW="lg" py={8}>
        <VStack spacing={8} align="stretch">
          <HStack justify="space-between">
            <Heading as="h1" size="xl" color="brand.500">
              <FiCloud style={{ display: 'inline', marginRight: 8 }} />
              Weather Dashboard
            </Heading>
            <IconButton
              aria-label="Settings"
              icon={<FiSettings />}
              size="md"
              colorScheme="brand"
              onClick={() => window.location.href = "/settings"}
            />
          </HStack>
          <Text fontSize="md" color="gray.600">
            Enter a city name to get the current weather. Make sure your API key is set in <b>Settings</b>.
          </Text>
          <HStack>
            <Input
              placeholder="Enter city (e.g. London)"
              value={city}
              onChange={e => setCity(e.target.value)}
              size="lg"
              bg={cardBg}
            />
            <Button
              colorScheme="brand"
              size="lg"
              onClick={fetchWeather}
              isDisabled={!city || loading}
            >
              Get Weather
            </Button>
          </HStack>
          {loading && <Spinner size="xl" color="brand.500" />}
          {error && (
            <Alert status="error" borderRadius="md">
              <AlertIcon />
              {error}
            </Alert>
          )}
          {weather && !error && (
            <Card bg={cardBg} shadow="md">
              <CardBody>
                <Heading size="md">{weather.city}</Heading>
                <Text fontSize="2xl" color="brand.500" mt={2}>
                  {weather.weather} - {weather.temperature}Â°C
                </Text>
                <Text color="gray.600">{weather.description}</Text>
                {weather.mock && (
                  <Text color="orange.400" fontWeight="bold" mt={2}>
                    (Mock data: set your API key in Settings for live weather)
                  </Text>
                )}
              </CardBody>
            </Card>
          )}
          {!weather && !loading && !error && (
            <Text color="gray.400" textAlign="center">
              No weather data yet. Try searching for a city!
            </Text>
          )}
          {history.length > 0 && (
            <Box>
              <Heading size="sm" mb={2}>Recent Searches</Heading>
              <HStack spacing={2}>
                {history.map((c, i) => (
                  <Button key={i} size="sm" variant="outline" onClick={() => { setCity(c); fetchWeather(); }}>
                    {c}
                  </Button>
                ))}
              </HStack>
            </Box>
          )}
        </VStack>
      </Container>
    </Box>
  )
}
