import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Heading,
  Text,
  VStack,
  Card,
  CardBody,
  CardHeader,
  FormControl,
  FormLabel,
  Input,
  Button,
  HStack,
  useColorModeValue,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalBody,
  ModalFooter,
  ModalCloseButton,
  useDisclosure,
  Switch,
  Alert,
  AlertIcon,
  IconButton,
} from '@chakra-ui/react';
import { FiPlus, FiTrash2 } from 'react-icons/fi';

const BACKEND_URL = import.meta.env.VITE_BACKEND_URL || "http://localhost:8001"

export default function SettingsPage() {
  const [keys, setKeys] = useState<{[service: string]: string}>({});
  const [apiKey, setApiKey] = useState("");
  const [useMock, setUseMock] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const { isOpen, onOpen, onClose } = useDisclosure();

  const cardBg = useColorModeValue('white', 'gray.800');

  const fetchKeys = async () => {
    setLoading(true);
    setError("");
    try {
      const resp = await fetch(`${BACKEND_URL}/settings/keys`);
      const data = await resp.json();
      setKeys(data);
    } catch (e) {
      setError("Failed to fetch keys.");
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchKeys();
    setUseMock(localStorage.getItem("USE_MOCK") === "true");
  }, []);

  const handleAddKey = async () => {
    setLoading(true);
    setError("");
    try {
      const resp = await fetch(`${BACKEND_URL}/settings/keys`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ service: "openweathermap", key: apiKey }),
      });
      if (!resp.ok) {
        const data = await resp.json();
        setError(data.detail || "Failed to add key.");
      } else {
        setApiKey("");
        onClose();
        fetchKeys();
      }
    } catch (e) {
      setError("Network error.");
    }
    setLoading(false);
  };

  const handleDeleteKey = async () => {
    setLoading(true);
    setError("");
    try {
      const resp = await fetch(`${BACKEND_URL}/settings/keys/openweathermap`, { method: "DELETE" });
      if (!resp.ok) {
        setError("Failed to delete key.");
      } else {
        fetchKeys();
      }
    } catch (e) {
      setError("Network error.");
    }
    setLoading(false);
  };

  const handleMockToggle = (checked: boolean) => {
    setUseMock(checked);
    localStorage.setItem("USE_MOCK", checked ? "true" : "false");
  };

  return (
    <Box minH="100vh" bg={useColorModeValue('gray.50', 'gray.900')}>
      <Container maxW="lg" py={8}>
        <VStack spacing={8} align="stretch">
          <Heading as="h1" size="xl" color="brand.500" textAlign="center">
            Weather App Settings
          </Heading>
          <Text fontSize="md" color="gray.600" textAlign="center">
            Manage your OpenWeatherMap API key and mock mode for demo/testing.
          </Text>
          <Card bg={cardBg} shadow="md">
            <CardHeader>
              <Heading size="md" color="brand.500">API Key Management</Heading>
            </CardHeader>
            <CardBody>
              <VStack spacing={4} align="stretch">
                <HStack justify="space-between">
                  <Text>OpenWeatherMap Key:</Text>
                  <HStack>
                    {keys.openweathermap ? (
                      <>
                        <Text fontFamily="mono">{keys.openweathermap}</Text>
                        <IconButton
                          aria-label="Delete key"
                          icon={<FiTrash2 />}
                          size="sm"
                          colorScheme="red"
                          onClick={handleDeleteKey}
                          isLoading={loading}
                        />
                      </>
                    ) : (
                      <Button
                        leftIcon={<FiPlus />}
                        colorScheme="brand"
                        size="sm"
                        onClick={onOpen}
                      >
                        Add Key
                      </Button>
                    )}
                  </HStack>
                </HStack>
                <HStack justify="space-between">
                  <Text>Use Mock Mode:</Text>
                  <Switch
                    isChecked={useMock}
                    onChange={e => handleMockToggle(e.target.checked)}
                    colorScheme="orange"
                  />
                </HStack>
                <Text fontSize="sm" color="gray.500">
                  Mock mode returns deterministic demo weather data. Turn off for live weather (requires API key).
                </Text>
                {error && (
                  <Alert status="error" borderRadius="md">
                    <AlertIcon />
                    {error}
                  </Alert>
                )}
              </VStack>
            </CardBody>
          </Card>
        </VStack>
      </Container>
      <Modal isOpen={isOpen} onClose={onClose} isCentered>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Add OpenWeatherMap API Key</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <FormControl>
              <FormLabel>API Key</FormLabel>
              <Input
                placeholder="Paste your OpenWeatherMap API key"
                value={apiKey}
                onChange={e => setApiKey(e.target.value)}
                autoFocus
              />
            </FormControl>
          </ModalBody>
          <ModalFooter>
            <Button colorScheme="brand" mr={3} onClick={handleAddKey} isLoading={loading} isDisabled={!apiKey}>
              Save Key
            </Button>
            <Button variant="ghost" onClick={onClose}>Cancel</Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </Box>
  );
}
</action>

