import React, { useEffect, useState } from 'react';
import {
  Box,
  Container,
  Heading,
  Text,
  VStack,
  Card,
  CardBody,
  Avatar,
  Button,
  HStack,
  useColorModeValue,
} from '@chakra-ui/react';
import { FiHome, FiSettings } from 'react-icons/fi';

export default function ProfilePage() {
  const bgColor = useColorModeValue('gray.50', 'gray.900');
  const cardBg = useColorModeValue('white', 'gray.800');
  const [history, setHistory] = useState<string[]>([]);

  useEffect(() => {
    const h = JSON.parse(localStorage.getItem("weather_history") || "[]");
    setHistory(h);
  }, []);

  return (
    <Box minH="100vh" bg={bgColor}>
      <Container maxW="lg" py={8}>
        <VStack spacing={8} align="stretch">
          <Heading as="h1" size="xl" color="brand.500" textAlign="center">
            Profile
          </Heading>
          <Card bg={cardBg} shadow="md">
            <CardBody>
              <VStack spacing={4}>
                <Avatar size="xl" name="Weather User" />
                <Text fontSize="lg" color="gray.600">Weather App User</Text>
                <Text fontSize="sm" color="gray.500">Your recent weather searches are shown below.</Text>
              </VStack>
            </CardBody>
          </Card>
          <Card bg={cardBg} shadow="md">
            <CardBody>
              <Heading size="md" mb={2}>Recent Weather Searches</Heading>
              {history.length > 0 ? (
                <HStack spacing={2}>
                  {history.map((c, i) => (
                    <Button key={i} size="sm" variant="outline">
                      {c}
