          <HStack justify="center" spacing={4}>
            <Button leftIcon={<FiHome />} colorScheme="brand" onClick={() => window.location.href = "/"}>
              Home
            </Button>
            <Button leftIcon={<FiSettings />} variant="outline" colorScheme="brand" onClick={() => window.location.href = "/settings"}>
              Settings
            </Button>
          </HStack>
        </VStack>
      </Container>
    </Box>
  );
}
