                    borderRadius="full"
                  />
                </InputGroup>
                <Button 
                  type="submit" 
                  colorScheme="blue" 
                  size="lg" 
                  borderRadius="full"
                  isLoading={loading}
                  loadingText="Searching"
                >
                  Search
                </Button>
              </VStack>
            </form>

            {/* Search Results */}
            {locations.length > 0 && (
              <Box mt={6}>
                <Heading size="md" mb={4}>Search Results</Heading>
                <List spacing={3}>
