First, let's create the component file with the necessary props and structure.

<action type="file" filePath="frontend/src/components/WeatherForm.tsx">
import React, { FormEvent } from "react";
import { HStack, Input, Button } from "@chakra-ui/react";
import { getWeatherByCity } from "../utils/weatherApi";
import { WeatherData } from "../types/weather";

interface WeatherFormProps {
  city: string;
  setCity: (city: string) => void;
  setWeather: (weather: WeatherData | null) => void;
  setLoading: (loading: boolean) => void;
  setError: (error: string | null) => void;
}

const WeatherForm: React.FC<WeatherFormProps> = ({
  city,
  setCity,
  setWeather,
  setLoading,
  setError,
}) => {
  const handleSubmit = async (e: FormEvent) => {
    e.preventDefault();
    if (!city.trim()) {
      setError("Please enter a city name.");
      setWeather(null);
      return;
    }
    setLoading(true);
    setError(null);
    setWeather(null);
    try {
      const data = await getWeatherByCity(city);
      setWeather(data);
    } catch (err: any) {
      setError(err.message || "Failed to fetch weather.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ width: "100%" }}>
      <HStack>
        <Input
          placeholder="Enter city name"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          size="lg"
        />
        <Button type="submit" colorScheme="blue" size="lg">
          Get Weather
        </Button>
      </HStack>
    </form>
  );
};

export default WeatherForm;
</action>

