Now let's create a service to communicate with our backend API:

<action type="file" filePath="frontend/src/services/todoService.ts">
const API_BASE = import.meta.env.VITE_API_URL;

if (!API_BASE) {
  throw new Error('VITE_API_URL is not defined in environment variables');
}

export const todoService = {
  // Get all todos
  async getAll(): Promise<Todo[]> {
    const response = await fetch(`${API_BASE}/todos/`);
    if (!response.ok) {
      throw new Error('Failed to fetch todos');
    }
    return response.json();
  },

  // Create a new todo
  async create(todo: TodoCreate): Promise<Todo> {
    const response = await fetch(`${API_BASE}/todos/`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(todo),
    });

    if (!response.ok) {
      throw new Error('Failed to create todo');
    }
    return response.json();
  },

  // Update a todo
  async update(id: string, todo: TodoUpdate): Promise<Todo> {
    const response = await fetch(`${API_BASE}/todos/${id}/`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(todo),
    });

    if (!response.ok) {
      throw new Error('Failed to update todo');
    }
    return response.json();
  },

  // Delete a todo
  async delete(id: string): Promise<void> {
    const response = await fetch(`${API_BASE}/todos/${id}/`, {
      method: 'DELETE',
    });

    if (!response.ok) {
      throw new Error('Failed to delete todo');
    }
  },
};
</action>

Let's create a TodoList component to display and manage todos:

<action type="file" filePath="frontend/src/components/TodoList.tsx">
import React, { useState, useEffect } from 'react';
import { Todo, TodoCreate, TodoUpdate } from '../types/todo';
import { todoService } from '../services/todoService';

const TodoList: React.FC = () => {
  const [todos, setTodos] = useState<Todo[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [newTodo, setNewTodo] = useState({ title: '', description: '' });
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editForm, setEditForm] = useState({ title: '', description: '' });

  // Fetch todos on component mount
  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    try {
      setLoading(true);
      const data = await todoService.getAll();
      setTodos(data);
      setError(null);
    } catch (err) {
      setError('Failed to fetch todos');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateTodo = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!newTodo.title.trim()) return;

    try {
      const todoData: TodoCreate = {
        title: newTodo.title,
        description: newTodo.description || undefined,
      };

      const newTodoItem = await todoService.create(todoData);
      setTodos([...todos, newTodoItem]);
      setNewTodo({ title: '', description: '' });
    } catch (err) {
      setError('Failed to create todo');
      console.error(err);
    }
  };

  const handleUpdateTodo = async (id: string) => {
    try {
      const todoData: TodoUpdate = {
        title: editForm.title,
        description: editForm.description || undefined,
      };

      const updatedTodo = await todoService.update(id, todoData);
      setTodos(todos.map(todo => todo.id === id ? updatedTodo : todo));
      setEditingId(null);
    } catch (err) {
      setError('Failed to update todo');
      console.error(err);
    }
  };

  const handleDeleteTodo = async (id: string) => {
    try {
      await todoService.delete(id);
      setTodos(todos.filter(todo => todo.id !== id));
    } catch (err) {
      setError('Failed to delete todo');
      console.error(err);
    }
  };

  const handleToggleComplete = async (id: string, completed: boolean) => {
    try {
      const updatedTodo = await todoService.update(id, { completed: !completed });
      setTodos(todos.map(todo => todo.id === id ? updatedTodo : todo));
    } catch (err) {
      setError('Failed to update todo');
      console.error(err);
    }
  };

  const startEditing = (todo: Todo) => {
    setEditingId(todo.id);
    setEditForm({
      title: todo.title,
      description: todo.description || '',
    });
  };

  const cancelEditing = () => {
    setEditingId(null);
  };

  if (loading) {
    return <div className="text-center py-8">Loading todos...</div>;
  }

  return (
    <div className="max-w-2xl mx-auto p-4">
      <h1 className="text-3xl font-bold text-center mb-8">Todo App</h1>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {/* Add Todo Form */}
      <form onSubmit={handleCreateTodo} className="mb-8 p-4 border rounded-lg shadow-sm">
        <h2 className="text-xl font-semibold mb-4">Add New Todo</h2>
        <div className="mb-4">
          <input
            type="text"
            placeholder="Todo title"
            value={newTodo.title}
            onChange={(e) => setNewTodo({ ...newTodo, title: e.target.value })}
            className="form-input w-full px-3 py-2 border rounded-md"
            required
          />
        </div>
        <div className="mb-4">
          <textarea
            placeholder="Description (optional)"
            value={newTodo.description}
            onChange={(e) => setNewTodo({ ...newTodo, description: e.target.value })}
            className="form-input w-full px-3 py-2 border rounded-md"
            rows={2}
          />
        </div>
        <button 
          type="submit" 
          className="btn btn-primary px-4 py-2 rounded-md"
        >
          Add Todo
        </button>
      </form>

      {/* Todo List */}
      <div>
        <h2 className="text-xl font-semibold mb-4">Your Todos ({todos.length})</h2>

        {todos.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            No todos yet. Add one above!
          </div>
        ) : (
          <ul className="space-y-3">
            {todos.map((todo) => (
              <li 
                key={todo.id} 
                className={`p-4 border rounded-lg shadow-sm ${todo.completed ? 'bg-green-50' : 'bg-white'}`}
              >
                {editingId === todo.id ? (
                  // Edit Form
                  <div className="space-y-3">
                    <input
                      type="text"
                      value={editForm.title}
                      onChange={(e) => setEditForm({ ...editForm, title: e.target.value })}
                      className="form-input w-full px-3 py-2 border rounded-md mb-2"
                      required
                    />
                    <textarea
                      value={editForm.description}
                      onChange={(e) => setEditForm({ ...editForm, description: e.target.value })}
                      className="form-input w-full px-3 py-2 border rounded-md mb-2"
                      rows={2}
                    />
                    <div className="flex space-x-2">
                      <button
                        onClick={() => handleUpdateTodo(todo.id)}
                        className="btn btn-primary px-3 py-1 rounded-md text-sm"
                      >
                        Save
                      </button>
                      <button
                        onClick={cancelEditing}
                        className="btn btn-secondary px-3 py-1 rounded-md text-sm"
                      >
                        Cancel
                      </button>
                    </div>
                  </div>
                ) : (
                  // Todo Display
                  <div>
                    <div className="flex items-start">
                      <input
                        type="checkbox"
                        checked={todo.completed}
                        onChange={() => handleToggleComplete(todo.id, todo.completed)}
                        className="mt-1 mr-3"
                      />
                      <div className="flex-1">
                        <h3 className={`font-medium ${todo.completed ? 'line-through text-gray-500' : ''}`}>
                          {todo.title}
                        </h3>
                        {todo.description && (
                          <p className={`text-gray-600 mt-1 ${todo.completed ? 'line-through' : ''}`}>
                            {todo.description}
                          </p>
                        )}
                        <p className="text-xs text-gray-400 mt-2">
                          Created: {new Date(todo.created_at).toLocaleDateString()}
                        </p>
                      </div>
                      <div className="flex space-x-2">
                        <button
                          onClick={() => startEditing(todo)}
                          className="text-blue-600 hover:text-blue-800"
                        >
                          Edit
                        </button>
                        <button
                          onClick={() => handleDeleteTodo(todo.id)}
                          className="text-red-600 hover:text-red-800"
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  </div>
                )}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default TodoList;
</action>

