const API_BASE = import.meta.env.VITE_API_URL;

export async function testApiConnection() {
  try {
    console.log('Testing API connection to:', API_BASE);

    // Test fetching todos
    const response = await fetch(`${API_BASE}/todos/`);
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const todos = await response.json();
    console.log('âœ… API Connection successful!');
    console.log('âœ… Todos fetched:', todos);

    // Test creating a todo
    const newTodo = {
      title: "Test Todo from Frontend",
      description: "This todo was created from the frontend",
      completed: false
    };

    const createResponse = await fetch(`${API_BASE}/todos/`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newTodo),
    });

    if (!createResponse.ok) {
      throw new Error(`HTTP error! status: ${createResponse.status}`);
    }

    const createdTodo = await createResponse.json();
    console.log('âœ… Todo created:', createdTodo);

    // Test deleting the todo
    const deleteResponse = await fetch(`${API_BASE}/todos/${createdTodo.id}/`, {
      method: 'DELETE',
    });

    if (!deleteResponse.ok) {
      throw new Error(`HTTP error! status: ${deleteResponse.status}`);
    }

