import { useLocation } from 'react-router-dom'
import { Home, Users, Mail, Settings, User } from "lucide-react"

import {
  Sidebar,
  SidebarContent,
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
} from "@/components/ui/sidebar"

// Define the item type
interface SidebarItem {
  title: string;
  url: string;
  icon: React.ComponentType<{ className?: string }>;
}

interface SidebarGroup {
  title: string;
  items: SidebarItem[];
}

// Route groups for organized sidebar
const routeGroups: SidebarGroup[] = [
  {
    title: "Overview",
    items: [
      { title: "Home", url: "/", icon: Home },
    ]
  },
  {
    title: "CRM",
    items: [
      { title: "Contacts", url: "/contacts", icon: Users },
      { title: "Campaigns", url: "/campaigns", icon: Mail },
    ]
  },
  {
    title: "Account",
    items: [
      { title: "Profile", url: "/profile", icon: User },
      { title: "Settings", url: "/settings", icon: Settings },
    ]
  },
]

export function AppSidebar() {
  const location = useLocation()

  const isActiveRoute = (url: string): boolean => {
    // Ensure we always return a boolean
    try {
      if (url === "/") return location.pathname === "/"
      if (url.includes(":")) {
        // Handle parameterized routes like /users/:id
        const baseRoute = url.split("/:")[0]
        return location.pathname.startsWith(baseRoute)
      }
      return location.pathname === url || location.pathname.startsWith(url + "/")
    } catch (error) {
      // In case of any error, default to false
      return false
    }
  }

  return (
    <Sidebar className="border-r bg-sidebar">
      <SidebarContent>
        {routeGroups.map((group) => (
          <SidebarGroup key={group.title}>
            <SidebarGroupLabel className="text-sidebar-foreground/70">
              {group.title}
            </SidebarGroupLabel>
            <SidebarGroupContent>
              <SidebarMenu>
                {group.items.map((item) => (
                  <SidebarMenuItem key={item.title}>
                    <SidebarMenuButton 
                      asChild
                      isActive={isActiveRoute(item.url)}
                      className="py-1.5"
                    >
                      <a href={item.url}>
                        <item.icon className="h-4 w-4" />
                        <span>{item.title}</span>
                      </a>
                    </SidebarMenuButton>
                  </SidebarMenuItem>
                ))}
              </SidebarMenu>
            </SidebarGroupContent>
          </SidebarGroup>
        ))}
      </SidebarContent>
    </Sidebar>
  )
}
```

