import { useState } from 'react';
import { ContactList } from '@/components/ContactList';
import { ContactForm } from '@/components/ContactForm';
import { ExcelUpload } from '@/components/ExcelUpload';
import { Button } from '@/components/ui/button';
import { PlusCircle, UploadIcon } from 'lucide-react';

export default function ContactsPage() {
  const [showForm, setShowForm] = useState(false);
  const [showUpload, setShowUpload] = useState(false);
  const [editingContact, setEditingContact] = useState(null);

  const handleCreate = () => {
    setEditingContact(null);
    setShowForm(true);
  };

  const handleEdit = (contact: any) => {
    setEditingContact(contact);
    setShowForm(true);
  };

  const handleFormSuccess = () => {
    setShowForm(false);
  };

  const handleUploadComplete = () => {
    // Refresh the contact list
    window.location.reload();
  };

  const handleUploadContacts = () => {
    setShowUpload(true);
  };

  return (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h1 className="text-3xl font-bold">Contacts</h1>
          <p className="text-muted-foreground">Manage your contact list</p>
        </div>
        <div className="flex flex-col sm:flex-row gap-2 w-full sm:w-auto">
          <Button onClick={handleUploadContacts} variant="outline" className="w-full sm:w-auto">
            <UploadIcon className="mr-2 h-4 w-4" />
            Upload Excel
          </Button>
          <Button onClick={handleCreate} className="w-full sm:w-auto">
            <PlusCircle className="mr-2 h-4 w-4" />
            Add Contact
          </Button>
        </div>
      </div>

      {showUpload ? (
        <div className="space-y-6">
          <ExcelUpload onUploadComplete={handleUploadComplete} />
          <Button variant="outline" onClick={() => setShowUpload(false)} className="w-full sm:w-auto">
            Back to Contacts
          </Button>
        </div>
      ) : showForm ? (
        <div className="bg-card rounded-lg border p-6">
          <h2 className="text-2xl font-bold mb-4">
            {editingContact ? 'Edit Contact' : 'Add Contact'}
          </h2>
          <ContactForm 
            contact={editingContact} 
            onSuccess={handleFormSuccess} 
            onCancel={() => setShowForm(false)}
          />
        </div>
      ) : (
        <ContactList onEdit={handleEdit} />
      )}
    </div>
  );
}
