import { useState, useEffect } from 'react';
import { CampaignCreate, Campaign } from '@/types/campaign';
import { campaignService } from '@/services/campaignService';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { toast } from 'sonner';
import { useEditor, EditorContent } from '@tiptap/react';
import StarterKit from '@tiptap/starter-kit';

interface CampaignFormProps {
  campaign: Campaign | null;
  onSuccess: () => void;
  onCancel: () => void;
}

export function CampaignForm({ campaign, onSuccess, onCancel }: CampaignFormProps) {
  const [name, setName] = useState(campaign?.name || '');
  const [subject, setSubject] = useState(campaign?.subject || '');
  const [tags, setTags] = useState(campaign?.tags.join(', ') || '');
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState<Record<string, string>>({});

  const editor = useEditor({
    extensions: [StarterKit],
    content: campaign?.content || '',
    editorProps: {
      attributes: {
        class: 'min-h-[200px] p-4 rounded-md border border-input bg-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 focus-visible:ring-offset-background',
      },
    },
  });

  useEffect(() => {
    if (editor && campaign?.content && editor.isEmpty) {
      editor.commands.setContent(campaign.content);
    }
  }, [editor, campaign?.content]);

  const validate = () => {
    const newErrors: Record<string, string> = {};
    if (!name.trim()) newErrors.name = 'Campaign name is required';
    if (!subject.trim()) newErrors.subject = 'Subject is required';
    if (!editor?.getText().trim()) newErrors.content = 'Content is required';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validate()) return;

    setLoading(true);
    try {
      const campaignData: CampaignCreate = {
        name,
        subject,
        content: editor?.getHTML() || '',
        tags: tags.split(',').map(tag => tag.trim()).filter(tag => tag)
      };

      if (campaign) {
        await campaignService.update(campaign.id, campaignData);
        toast.success('Campaign updated successfully');
      } else {
        await campaignService.create(campaignData);
        toast.success('Campaign created successfully');
      }

      onSuccess();
    } catch (err) {
      console.error(err);
      toast.error(campaign ? 'Failed to update campaign' : 'Failed to create campaign');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div className="space-y-4">
        <div>
          <Label htmlFor="name">Campaign Name</Label>
          <Input
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Enter campaign name"
            className={errors.name ? 'border-red-500' : ''}
          />
          {errors.name && <p className="text-sm text-red-500 mt-1">{errors.name}</p>}
        </div>

        <div>
          <Label htmlFor="subject">Subject</Label>
          <Input
            id="subject"
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
            placeholder="Enter email subject"
            className={errors.subject ? 'border-red-500' : ''}
          />
          {errors.subject && <p className="text-sm text-red-500 mt-1">{errors.subject}</p>}
        </div>

        <div>
          <Label htmlFor="content">Content</Label>
          {editor && <EditorContent editor={editor} />}
          {errors.content && <p className="text-sm text-red-500 mt-1">{errors.content}</p>}
        </div>

        <div>
          <Label htmlFor="tags">Tags</Label>
          <Input
            id="tags"
            value={tags}
            onChange={(e) => setTags(e.target.value)}
            placeholder="Enter tags separated by commas"
          />
          <p className="text-sm text-muted-foreground mt-1">
            Separate tags with commas (e.g. prospect, customer, vip)
          </p>
        </div>
      </div>

      <div className="flex justify-end gap-3 pt-4">
        <Button
          type="button"
          variant="outline"
          onClick={onCancel}
          disabled={loading}
        >
          Cancel
        </Button>
        <Button type="submit" disabled={loading}>
          {loading ? 'Saving...' : (campaign ? 'Update Campaign' : 'Create Campaign')}
        </Button>
      </div>
    </form>
  );
}
