  SelectValue,
} from "@/components/ui/select";
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/components/ui/tooltip";

export function CampaignList({ onEdit }: { onEdit: (campaign: Campaign | null) => void }) {
  const [campaigns, setCampaigns] = useState<Campaign[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState<'name' | 'date' | 'status'>('date');
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('desc');
  const [filterStatus, setFilterStatus] = useState<string>('all');
  const [uniqueTags, setUniqueTags] = useState<string[]>([]);

  const fetchCampaigns = async () => {
    try {
      setLoading(true);
      const data = await campaignService.getAll();
      setCampaigns(data);

      // Extract unique tags
      const tags = new Set<string>();
      data.forEach(campaign => {
        campaign.tags.forEach(tag => tags.add(tag));
      });
      setUniqueTags(Array.from(tags));

      setError(null);
    } catch (err) {
      setError('Failed to fetch campaigns');
      console.error(err);
      toast.error('Failed to fetch campaigns');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCampaigns();
  }, []);

  const handleDelete = async (id: string) => {
    if (window.confirm('Are you sure you want to delete this campaign?')) {
      try {
        await campaignService.delete(id);
        fetchCampaigns(); // Refresh the list
        toast.success('Campaign deleted successfully');
      } catch (err) {
        setError('Failed to delete campaign');
        toast.error('Failed to delete campaign');
        console.error(err);
      }
    }
  };

  const handleSend = async (id: string, name: string) => {
    if (window.confirm(`Are you sure you want to send campaign "${name}"?`)) {
      try {
        const result = await campaignService.sendCampaign(id);
        fetchCampaigns(); // Refresh the list
        toast.success(result.message);
      } catch (err) {
        setError('Failed to send campaign');
        toast.error('Failed to send campaign');
        console.error(err);
      }
    }
  };

  // Filter and sort campaigns
  const processedCampaigns = (() => {
    let result = [...campaigns];

    // Apply search filter
    if (searchTerm) {
      result = result.filter(campaign => 
        campaign.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        campaign.subject.toLowerCase().includes(searchTerm.toLowerCase()) ||
        campaign.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }

    // Apply status filter
    if (filterStatus !== 'all') {
      result = result.filter(campaign => {
        if (filterStatus === 'sent') return campaign.sent;
        if (filterStatus === 'draft') return !campaign.sent;
        return true;
      });
    }

    // Apply sorting
    result.sort((a, b) => {
      let comparison = 0;
      switch (sortBy) {
        case 'name':
          comparison = a.name.localeCompare(b.name);
          break;
        case 'date':
          comparison = new Date(a.created_at).getTime() - new Date(b.created_at).getTime();
          break;
        case 'status':
          comparison = (a.sent ? 1 : 0) - (b.sent ? 1 : 0);
          break;
      }
      return sortOrder === 'asc' ? comparison : -comparison;
    });

    return result;
  })();

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
