              <DropdownMenuSeparator />
              <DropdownMenuItem 
                onClick={() => { setSortBy('name'); setSortOrder('asc'); }}
                className={sortBy === 'name' && sortOrder === 'asc' ? 'bg-muted' : ''}
              >
                Name (A-Z)
              </DropdownMenuItem>
              <DropdownMenuItem 
                onClick={() => { setSortBy('name'); setSortOrder('desc'); }}
                className={sortBy === 'name' && sortOrder === 'desc' ? 'bg-muted' : ''}
              >
                Name (Z-A)
              </DropdownMenuItem>
              <DropdownMenuItem 
                onClick={() => { setSortBy('date'); setSortOrder('desc'); }}
                className={sortBy === 'date' && sortOrder === 'desc' ? 'bg-muted' : ''}
              >
                Newest First
              </DropdownMenuItem>
              <DropdownMenuItem 
                onClick={() => { setSortBy('date'); setSortOrder('asc'); }}
                className={sortBy === 'date' && sortOrder === 'asc' ? 'bg-muted' : ''}
              >
                Oldest First
              </DropdownMenuItem>
              <DropdownMenuItem 
                onClick={() => { setSortBy('status'); setSortOrder('desc'); }}
                className={sortBy === 'status' && sortOrder === 'desc' ? 'bg-muted' : ''}
              >
                Status (Sent first)
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>

      {/* Results Summary */}
      <div className="flex justify-between items-center">
        <p className="text-sm text-muted-foreground">
