  SidebarMenuItem,
} from "@/components/ui/sidebar"

// Define the item type
interface SidebarItem {
  title: string;
  url: string;
  icon: React.ComponentType<{ className?: string }>;
}

interface SidebarGroup {
  title: string;
  items: SidebarItem[];
}

// Route groups for organized sidebar
const routeGroups: SidebarGroup[] = [
  {
    title: "Overview",
    items: [
      { title: "Home", url: "/", icon: Home },
    ]
  },
  {
    title: "CRM",
    items: [
      { title: "Contacts", url: "/contacts", icon: Users },
      { title: "Campaigns", url: "/campaigns", icon: Mail },
    ]
  },
  {
    title: "Account",
    items: [
      { title: "Profile", url: "/profile", icon: User },
      { title: "Settings", url: "/settings", icon: Settings },
    ]
  },
]

export function AppSidebar() {
  const location = useLocation()

  // Function to determine if a route is active
  const isActiveRoute = (url: string) => {
    if (url === "/") {
      return location.pathname === "/"
    }
    // For other routes, check if the current path starts with the route url
    return location.pathname.startsWith(url)
  }

  return (
    <Sidebar className="border-r bg-sidebar">
      <SidebarContent>
        {routeGroups.map((group) => (
          <SidebarGroup key={group.title}>
            <SidebarGroupLabel className="text-sidebar-foreground/70 font-medium text-xs uppercase tracking-wider">
              {group.title}
            </SidebarGroupLabel>
            <SidebarGroupContent>
              <SidebarMenu>
                {group.items.map((item) => {
                  const isActive = isActiveRoute(item.url)
                  return (
                    <SidebarMenuItem key={item.title}>
                      <SidebarMenuButton 
                        asChild
                        isActive={isActive}
                        className="py-1.5"
                      >
                        <a href={item.url}>
                          <item.icon className={`h-4 w-4 ${isActive ? 'text-sidebar-primary' : ''}`} />
                          <span>{item.title}</span>
                        </a>
                      </SidebarMenuButton>
                    </SidebarMenuItem>
                  )
                })}
              </SidebarMenu>
            </SidebarGroupContent>
          </SidebarGroup>
        ))}
      </SidebarContent>
    </Sidebar>
  )
}
