from sqlalchemy.orm import Session
from models.crm_models import Contact, Tag, Campaign, SentEmail, contact_tag_association
from models.crm_schemas import ContactCreate, ContactUpdate, TagCreate, TagUpdate, CampaignCreate, CampaignUpdate
from typing import List, Optional
from datetime import datetime

# Contact CRUD operations
def get_contact(db: Session, contact_id: int) -> Optional[Contact]:
    return db.query(Contact).filter(Contact.id == contact_id).first()

def get_contact_by_email(db: Session, email: str) -> Optional[Contact]:
    return db.query(Contact).filter(Contact.email == email).first()

def get_contacts(db: Session, skip: int = 0, limit: int = 100) -> List[Contact]:
    return db.query(Contact).offset(skip).limit(limit).all()

def get_contacts_by_tag(db: Session, tag_id: int) -> List[Contact]:
    return db.query(Contact).join(contact_tag_association).filter(
        contact_tag_association.c.tag_id == tag_id
    ).all()

def create_contact(db: Session, contact: ContactCreate) -> Contact:
    db_contact = Contact(
        first_name=contact.first_name,
        last_name=contact.last_name,
        email=contact.email,
        phone=contact.phone,
        company=contact.company,
        position=contact.position
    )
    db.add(db_contact)
    db.commit()
    db.refresh(db_contact)

    # Associate tags if provided
    if contact.tag_ids:
        tags = db.query(Tag).filter(Tag.id.in_(contact.tag_ids)).all()
        db_contact.tags.extend(tags)
        db.commit()
        db.refresh(db_contact)

    return db_contact

def update_contact(db: Session, contact_id: int, contact: ContactUpdate) -> Contact:
    db_contact = get_contact(db, contact_id)
    if not db_contact:
        return None

    # Update contact fields
    db_contact.first_name = contact.first_name
    db_contact.last_name = contact.last_name
    db_contact.email = contact.email
    db_contact.phone = contact.phone
    db_contact.company = contact.company
    db_contact.position = contact.position

    # Update tag associations
    if contact.tag_ids is not None:
        tags = db.query(Tag).filter(Tag.id.in_(contact.tag_ids)).all()
        db_contact.tags = tags

    db.commit()
    db.refresh(db_contact)
    return db_contact

def delete_contact(db: Session, contact_id: int) -> bool:
    db_contact = get_contact(db, contact_id)
    if not db_contact:
        return False
    db.delete(db_contact)
    db.commit()
    return True

# Tag CRUD operations
def get_tag(db: Session, tag_id: int) -> Optional[Tag]:
    return db.query(Tag).filter(Tag.id == tag_id).first()

def get_tag_by_name(db: Session, name: str) -> Optional[Tag]:
    return db.query(Tag).filter(Tag.name == name).first()

def get_tags(db: Session, skip: int = 0, limit: int = 100) -> List[Tag]:
    return db.query(Tag).offset(skip).limit(limit).all()

def create_tag(db: Session, tag: TagCreate) -> Tag:
    db_tag = Tag(name=tag.name, color=tag.color)
    db.add(db_tag)
    db.commit()
    db.refresh(db_tag)
    return db_tag

def update_tag(db: Session, tag_id: int, tag: TagUpdate) -> Tag:
    db_tag = get_tag(db, tag_id)
    if not db_tag:
        return None

    db_tag.name = tag.name
    db_tag.color = tag.color
    db.commit()
    db.refresh(db_tag)
    return db_tag

def delete_tag(db: Session, tag_id: int) -> bool:
    db_tag = get_tag(db, tag_id)
    if not db_tag:
        return False
    db.delete(db_tag)
    db.commit()
    return True

# Campaign CRUD operations
def get_campaign(db: Session, campaign_id: int) -> Optional[Campaign]:
    return db.query(Campaign).filter(Campaign.id == campaign_id).first()

def get_campaigns(db: Session, skip: int = 0, limit: int = 100) -> List[Campaign]:
    return db.query(Campaign).offset(skip).limit(limit).all()

def create_campaign(db: Session, campaign: CampaignCreate) -> Campaign:
    db_campaign = Campaign(
        name=campaign.name,
        subject=campaign.subject,
        content=campaign.content,
        tag_id=campaign.tag_id
    )
    db.add(db_campaign)
    db.commit()
    db.refresh(db_campaign)
    return db_campaign

def update_campaign(db: Session, campaign_id: int, campaign: CampaignUpdate) -> Campaign:
    db_campaign = get_campaign(db, campaign_id)
    if not db_campaign:
        return None

    db_campaign.name = campaign.name
    db_campaign.subject = campaign.subject
    db_campaign.content = campaign.content
    db_campaign.tag_id = campaign.tag_id
    db.commit()
    db.refresh(db_campaign)
    return db_campaign

def delete_campaign(db: Session, campaign_id: int) -> bool:
    db_campaign = get_campaign(db, campaign_id)
    if not db_campaign:
        return False
    db.delete(db_campaign)
    db.commit()
    return True

def send_campaign(db: Session, campaign_id: int) -> Campaign:
    db_campaign = get_campaign(db, campaign_id)
    if not db_campaign:
        return None

    # Mark campaign as sent
    db_campaign.sent_at = datetime.utcnow()
    db.commit()
    db.refresh(db_campaign)
    return db_campaign

# Sent Email operations
def get_sent_emails(db: Session, skip: int = 0, limit: int = 100) -> List[SentEmail]:
    return db.query(SentEmail).offset(skip).limit(limit).all()

def create_sent_email(db: Session, contact_id: int, campaign_id: int) -> SentEmail:
    db_sent_email = SentEmail(
        contact_id=contact_id,
        campaign_id=campaign_id
    )
    db.add(db_sent_email)
    db.commit()
    db.refresh(db_sent_email)
    return db_sent_email

def get_sent_emails_by_campaign(db: Session, campaign_id: int) -> List[SentEmail]:
    return db.query(SentEmail).filter(SentEmail.campaign_id == campaign_id).all()
</action>

Now let me create the API routes for our CRM:

```xml
<action type="file" path="backend/routes/crm_routes.py">
from fastapi import APIRouter, Depends, HTTPException
from sqlalchemy.orm import Session
from database import get_db
from models.crm_schemas import *
from services.crm_service import *
from typing import List

router = APIRouter(prefix="/api", tags=["crm"])

# Contact routes
@router.get("/contacts/", response_model=List[Contact])
def read_contacts(skip: int = 0, limit: int = 100, db: Session = Depends(get_db)):
    contacts = get_contacts(db, skip=skip, limit=limit)
    return contacts

@router.get("/contacts/{contact_id}", response_model=Contact)
def read_contact(contact_id: int, db: Session = Depends(get_db)):
    db_contact = get_contact(db, contact_id=contact_id)
    if db_contact is None:
        raise HTTPException(status_code=404, detail="Contact not found")
    return db_contact

@router.post("/contacts/", response_model=Contact)
def create_contact(contact: ContactCreate, db: Session = Depends(get_db)):
    db_contact = get_contact_by_email(db, email=contact.email)
    if db_contact:
        raise HTTPException(status_code=400, detail="Contact with this email already exists")
    return create_contact(db=db, contact=contact)

@router.put("/contacts/{contact_id}", response_model=Contact)
def update_contact(contact_id: int, contact: ContactUpdate, db: Session = Depends(get_db)):
    db_contact = get_contact(db, contact_id=contact_id)
    if db_contact is None:
        raise HTTPException(status_code=404, detail="Contact not found")
    return update_contact(db=db, contact_id=contact_id, contact=contact)

@router.delete("/contacts/{contact_id}")
def delete_contact(contact_id: int, db: Session = Depends(get_db)):
    success = delete_contact(db, contact_id=contact_id)
    if not success:
        raise HTTPException(status_code=404, detail="Contact not found")
    return {"message": "Contact deleted successfully"}

# Tag routes
@router.get("/tags/", response_model=List[Tag])
def read_tags(skip: int = 0, limit: int = 100, db: Session = Depends(get_db)):
    tags = get_tags(db, skip=skip, limit=limit)
    return tags

@router.get("/tags/{tag_id}", response_model=Tag)
def read_tag(tag_id: int, db: Session = Depends(get_db)):
    db_tag = get_tag(db, tag_id=tag_id)
    if db_tag is None:
        raise HTTPException(status_code=404, detail="Tag not found")
    return db_tag

@router.post("/tags/", response_model=Tag)
def create_tag(tag: TagCreate, db: Session = Depends(get_db)):
    db_tag = get_tag_by_name(db, name=tag.name)
    if db_tag:
        raise HTTPException(status_code=400, detail="Tag with this name already exists")
    return create_tag(db=db, tag=tag)

@router.put("/tags/{tag_id}", response_model=Tag)
def update_tag(tag_id: int, tag: TagUpdate, db: Session = Depends(get_db)):
    db_tag = get_tag(db, tag_id=tag_id)
    if db_tag is None:
        raise HTTPException(status_code=404, detail="Tag not found")
    return update_tag(db=db, tag_id=tag_id, tag=tag)

@router.delete("/tags/{tag_id}")
def delete_tag(tag_id: int, db: Session = Depends(get_db)):
    success = delete_tag(db, tag_id=tag_id)
    if not success:
        raise HTTPException(status_code=404, detail="Tag not found")
    return {"message": "Tag deleted successfully"}

# Campaign routes
@router.get("/campaigns/", response_model=List[Campaign])
def read_campaigns(skip: int = 0, limit: int = 100, db: Session = Depends(get_db)):
    campaigns = get_campaigns(db, skip=skip, limit=limit)
    return campaigns

@router.get("/campaigns/{campaign_id}", response_model=Campaign)
def read_campaign(campaign_id: int, db: Session = Depends(get_db)):
    db_campaign = get_campaign(db, campaign_id=campaign_id)
    if db_campaign is None:
        raise HTTPException(status_code=404, detail="Campaign not found")
    return db_campaign

@router.post("/campaigns/", response_model=Campaign)
def create_campaign(campaign: CampaignCreate, db: Session = Depends(get_db)):
    return create_campaign(db=db, campaign=campaign)

@router.put("/campaigns/{campaign_id}", response_model=Campaign)
def update_campaign(campaign_id: int, campaign: CampaignUpdate, db: Session = Depends(get_db)):
    db_campaign = get_campaign(db, campaign_id=campaign_id)
    if db_campaign is None:
        raise HTTPException(status_code=404, detail="Campaign not found")
    return update_campaign(db=db, campaign_id=campaign_id, campaign=campaign)

@router.delete("/campaigns/{campaign_id}")
def delete_campaign(campaign_id: int, db: Session = Depends(get_db)):
    success = delete_campaign(db, campaign_id=campaign_id)
    if not success:
        raise HTTPException(status_code=404, detail="Campaign not found")
    return {"message": "Campaign deleted successfully"}

# Campaign sending route
@router.post("/campaigns/{campaign_id}/send")
def send_campaign(campaign_id: int, db: Session = Depends(get_db)):
    db_campaign = get_campaign(db, campaign_id=campaign_id)
    if db_campaign is None:
        raise HTTPException(status_code=404, detail="Campaign not found")

    # Get all contacts with the campaign's tag
    contacts = get_contacts_by_tag(db, tag_id=db_campaign.tag_id)

    # Create sent email records for each contact
    for contact in contacts:
        create_sent_email(db, contact_id=contact.id, campaign_id=campaign_id)

    # Mark campaign as sent
    updated_campaign = send_campaign(db, campaign_id=campaign_id)

    return {
        "message": f"Successfully sent campaign to {len(contacts)} contacts",
        "campaign": updated_campaign
    }

# Sent Email routes
@router.get("/sent-emails/", response_model=List[SentEmail])
def read_sent_emails(skip: int = 0, limit: int = 100, db: Session = Depends(get_db)):
    sent_emails = get_sent_emails(db, skip=skip, limit=limit)
    return sent_emails

@router.get("/campaigns/{campaign_id}/sent-emails", response_model=List[SentEmail])
def read_sent_emails_by_campaign(campaign_id: int, db: Session = Depends(get_db)):
    sent_emails = get_sent_emails_by_campaign(db, campaign_id=campaign_id)
    return sent_emails
</action>
