Let me create a simple test for the contact-tag relationship:

<action type="file" filePath="backend/test_contact_tags.py">
import json
import urllib.request
import urllib.error

# API base URL
API_URL = "http://localhost:8003/api"

def make_request(url, method="GET", data=None):
    """Helper function to make HTTP requests"""
    if data:
        data = json.dumps(data).encode('utf-8')
        req = urllib.request.Request(url, data=data, method=method)
        req.add_header('Content-Type', 'application/json')
    else:
        req = urllib.request.Request(url, method=method)

    try:
        response = urllib.request.urlopen(req)
        return json.loads(response.read())
    except urllib.error.HTTPError as e:
        print(f"HTTP Error {e.code}: {e.reason}")
        print(e.read().decode())
        return None
    except Exception as e:
        print(f"Error: {e}")
        return None

def test_contact_tags():
    """Test contact-tag relationships"""
    print("=== Testing Contact-Tag Relationships ===")

    # Add tag to contact
    print("1. Adding tag to contact...")
    contact_tag_data = {
        "contact_id": 1,
        "tag_id": 1
    }
    result = make_request(f"{API_URL}/contact-tags/", "POST", contact_tag_data)
    if result:
        print(f"   {result['message']}")
    else:
        print("   Failed to add tag to contact")
        return

    # Get contact to verify tag is added
    print("2. Verifying tag is added to contact...")
    result = make_request(f"{API_URL}/contacts/1")
    if result:
        print(f"   Contact: {result['name']}")
        if 'tags' in result and result['tags']:
            print(f"   Tags: {[tag['name'] for tag in result['tags']]}")
        else:
            print("   No tags found")

    # Remove tag from contact
    print("3. Removing tag from contact...")
    result = make_request(f"{API_URL}/contact-tags/", "DELETE", contact_tag_data)
    if result:
        print(f"   {result['message']}")
    else:
        print("   Failed to remove tag from contact")

if __name__ == "__main__":
    test_contact_tags()
</action>

