        toast({
          title: 'Error',
          description: error.detail || 'Failed to save tag',
          status: 'error',
          duration: 5000,
          isClosable: true,
        });
      }
    } catch (error) {
      console.error('Error saving tag:', error);
      toast({
        title: 'Error',
        description: 'Failed to save tag. Please try again.',
        status: 'error',
        duration: 5000,
        isClosable: true,
