              <Th>Actions</Th>
            </Tr>
          </Thead>
          <Tbody>
            {campaigns.map((campaign) => (
              <Tr key={campaign.id}>
                <Td fontWeight="bold">{campaign.name}</Td>
                <Td>{campaign.subject}</Td>
                <Td>
                  <Badge colorScheme={getStatusColor(campaign.status)}>
                    {campaign.status}
