import React from 'react';
import {
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  Tag,
  TagLabel,
  IconButton,
  Wrap,
  WrapItem,
  HStack,
  Select
} from '@chakra-ui/react';
import { EditIcon, DeleteIcon } from '@chakra-ui/icons';

interface TagType {
  id: number;
  name: string;
  color: string;
}

interface ContactType {
  id: number;
  name: string;
  email: string;
  phone: string | null;
  company: string | null;
  position: string | null;
  notes: string | null;
  created_at: string;
  tags: TagType[];
}

interface ContactTableProps {
  contacts: ContactType[];
  tags: TagType[];
  onEdit: (contact: ContactType) => void;
  onDelete: (contactId: number) => void;
  onAddTag: (contactId: number, tagId: number) => void;
  onRemoveTag: (contactId: number, tagId: number) => void;
}

const formatDate = (dateString: string) => {
  return new Date(dateString).toLocaleDateString();
};

const ContactTable: React.FC<ContactTableProps> = ({
  contacts,
  tags,
  onEdit,
  onDelete,
  onAddTag,
  onRemoveTag
}) => {
  return (
    <Table variant="simple">
      <Thead>
        <Tr>
          <Th>Name</Th>
          <Th>Email</Th>
          <Th>Phone</Th>
          <Th>Company</Th>
          <Th>Tags</Th>
          <Th>Created</Th>
          <Th>Actions</Th>
        </Tr>
      </Thead>
      <Tbody>
        {contacts.map((contact) => (
          <Tr key={contact.id}>
            <Td>{contact.name}</Td>
            <Td>{contact.email}</Td>
            <Td>{contact.phone || 'N/A'}</Td>
            <Td>{contact.company || 'N/A'}</Td>
            <Td>
              <Wrap>
                {contact.tags.map((tag) => (
                  <WrapItem key={tag.id}>
                    <HStack spacing={1}>
                      <Tag size="sm" colorScheme={tag.color || 'gray'}>
                        <TagLabel>{tag.name}</TagLabel>
                      </Tag>
                      <IconButton
                        aria-label="Remove tag"
                        icon={<DeleteIcon />}
                        size="xs"
                        variant="ghost"
                        onClick={() => onRemoveTag(contact.id, tag.id)}
                      />
                    </HStack>
                  </WrapItem>
                ))}
                <WrapItem>
                  <Select
                    placeholder="Add tag"
                    size="sm"
                    width="120px"
                    onChange={(e) => {
                      if (e.target.value) {
                        onAddTag(contact.id, parseInt(e.target.value));
                        e.target.value = '';
                      }
                    }}
                  >
                    {tags
                      .filter(tag => !contact.tags.some(contactTag => contactTag.id === tag.id))
                      .map((tag) => (
                        <option key={tag.id} value={tag.id}>
                          {tag.name}
                        </option>
                      ))}
                  </Select>
                </WrapItem>
              </Wrap>
            </Td>
            <Td>{formatDate(contact.created_at)}</Td>
            <Td>
              <HStack spacing={2}>
                <IconButton
                  aria-label="Edit contact"
                  icon={<EditIcon />}

