<action type="file" filePath="frontend/src/components/contacts/BulkImportModal.tsx">
<content>
import React, { useState } from 'react';
import {
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  ModalFooter,
  Button,
  FormControl,
  FormLabel,
  Input,
  Select,
  VStack,
  Alert,
  AlertIcon,
  AlertTitle,
  AlertDescription,
  Box,
  Text
} from '@chakra-ui/react';

interface TagType {
  id: number;
  name: string;
  color: string;
}

interface BulkImportModalProps {
  isOpen: boolean;
  onClose: () => void;
  onImport: (file: File, tagIds: number[]) => Promise<{ success: boolean; message: string; errors?: string[] }>;
  tags: TagType[];
}

const BulkImportModal: React.FC<BulkImportModalProps> = ({ isOpen, onClose, onImport, tags }) => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [selectedTagIds, setSelectedTagIds] = useState<number[]>([]);
  const [isImporting, setIsImporting] = useState(false);
  const [importResult, setImportResult] = useState<{ success: boolean; message: string; errors?: string[] } | null>(null);

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      setSelectedFile(e.target.files[0]);
      setImportResult(null);
    }
  };

  const handleTagChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const selectedOptions = Array.from(e.target.selectedOptions, option => parseInt(option.value));
    setSelectedTagIds(selectedOptions);
  };

  const handleImport = async () => {
    if (!selectedFile) {
      setImportResult({
        success: false,
        message: 'Please select a file to import'
      });
      return;
    }

    setIsImporting(true);
    try {
      const result = await onImport(selectedFile, selectedTagIds);
      setImportResult(result);
      if (result.success) {
        // Reset form on successful import
        setSelectedFile(null);
        setSelectedTagIds([]);
      }
    } catch (error) {
      setImportResult({
        success: false,
        message: 'An unexpected error occurred during import'
      });
    } finally {
      setIsImporting(false);
    }
  };

  const handleClose = () => {
    onClose();
    // Reset form when closing
    setTimeout(() => {
      setSelectedFile(null);
      setSelectedTagIds([]);
      setImportResult(null);
      setIsImporting(false);
    }, 300);
  };

  return (
    <Modal isOpen={isOpen} onClose={handleClose} size="lg">
      <ModalOverlay />
      <ModalContent>
        <ModalHeader>Bulk Import Contacts</ModalHeader>
        <ModalCloseButton />
        <ModalBody>
          <VStack spacing={4} align="stretch">
            <FormControl isRequired>
              <FormLabel>Upload CSV or Excel File</FormLabel>
              <Input
                type="file"
                accept=".csv,.xlsx,.xls"
                onChange={handleFileChange}
                isDisabled={isImporting}
              />
              <Text fontSize="sm" color="gray.500" mt={2}>
                File should contain columns: name, email (required) and optionally phone, company, position, notes
              </Text>
            </FormControl>

            <FormControl>
              <FormLabel>Assign Tags (Optional)</FormLabel>
              <Select
                multiple
                value={selectedTagIds.map(String)}
                onChange={handleTagChange}
                isDisabled={isImporting}
                height="150px"
              >
                {tags.map((tag) => (
                  <option key={tag.id} value={tag.id}>
                    {tag.name}
                  </option>
                ))}
              </Select>
              <Text fontSize="sm" color="gray.500" mt={2}>
                Select tags to assign to all imported contacts
              </Text>
            </FormControl>

            {importResult && (
              <Alert status={importResult.success ? 'success' : 'error'}>
                <AlertIcon />
                <Box flex="1">
                  <AlertTitle>{importResult.success ? 'Success!' : 'Import Failed'}</AlertTitle>
                  <AlertDescription display="block">
                    {importResult.message}
                    {importResult.errors && importResult.errors.length > 0 && (
                      <>
                        <br />
                        <strong>Errors:</strong>
                        <ul>
                          {importResult.errors.map((error, index) => (
                            <li key={index}>{error}</li>
                          ))}
                        </ul>
                      </>
                    )}
                  </AlertDescription>
                </Box>
              </Alert>
            )}
          </VStack>
        </ModalBody>

        <ModalFooter>
          <Button variant="ghost" mr={3} onClick={handleClose} isDisabled={isImporting}>
            Cancel
          </Button>
          <Button
            colorScheme="blue"
            onClick={handleImport}
            isLoading={isImporting}
            isDisabled={!selectedFile || isImporting}
          >
            Import Contacts
          </Button>
        </ModalFooter>
      </ModalContent>
    </Modal>
  );
};

export default BulkImportModal;
</content>
</action>

