<action type="update_file" path="frontend/src/pages/ContactsPage.tsx">
<content>
import React, { useState, useEffect } from 'react';
import {
  Box,
  Button,
  Heading,
  HStack,
  useToast,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  ModalFooter,
  VStack
} from '@chakra-ui/react';
import { AddIcon } from '@chakra-ui/icons';
import ContactTable from '../components/contacts/ContactTable';
import ContactForm from '../components/contacts/ContactForm';
import BulkImportModal from '../components/contacts/BulkImportModal';
import SearchAndFilter from '../components/contacts/SearchAndFilter';

interface TagType {
  id: number;
  name: string;
  color: string;
}

interface ContactType {
  id: number;
  name: string;
  email: string;
  phone: string | null;
  company: string | null;
  position: string | null;
  notes: string | null;
  created_at: string;
  tags: TagType[];
}

const ContactsPage: React.FC = () => {
  const [contacts, setContacts] = useState<ContactType[]>([]);
  const [tags, setTags] = useState<TagType[]>([]);
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [isBulkImportModalOpen, setIsBulkImportModalOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedTag, setSelectedTag] = useState<number | ''>('');
  const [contactToEdit, setContactToEdit] = useState<ContactType | null>(null);
  const [newContact, setNewContact] = useState({
    name: '',
    email: '',
    phone: '',
    company: '',
    position: '',
    notes: ''
  });

  const toast = useToast();
  const apiUrl = import.meta.env.VITE_API_URL || 'http://localhost:8000';

  // Fetch contacts and tags
  useEffect(() => {
    fetchContacts();
    fetchTags();
  }, [searchTerm, selectedTag]);

  const fetchContacts = async () => {
    try {
      const response = await fetch(
        `${apiUrl}/api/contacts/?search=${searchTerm}${selectedTag ? `&tag_id=${selectedTag}` : ''}`
      );
      if (response.ok) {
        const data = await response.json();
        setContacts(data);
      }
    } catch (error) {
      console.error('Error fetching contacts:', error);
    }
  };

  const fetchTags = async () => {
    try {
      const response = await fetch(`${apiUrl}/api/tags/`);
      if (response.ok) {
        const data = await response.json();
        setTags(data);
      }
    } catch (error) {
      console.error('Error fetching tags:', error);
    }
  };

  const handleCreateContact = async () => {
    try {
      const response = await fetch(`${apiUrl}/api/contacts/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newContact),
      });

      if (response.ok) {
        const contact = await response.json();
        setContacts([...contacts, { ...contact, tags: [] }]);
        setIsCreateModalOpen(false);
        setNewContact({
          name: '',
          email: '',
          phone: '',
          company: '',
          position: '',
          notes: ''
        });
        toast({
          title: 'Contact created',
          description: 'Your contact has been created successfully.',
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
      } else {
        const errorData = await response.json();
        toast({
          title: 'Error creating contact',
          description: errorData.detail || 'There was an error creating your contact.',
          status: 'error',
          duration: 3000,
          isClosable: true,
        });
      }
    } catch (error) {
      console.error('Error creating contact:', error);
      toast({
        title: 'Error',
        description: 'Failed to create contact',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    }
  };

  const handleUpdateContact = async () => {
    if (!contactToEdit) return;

    try {
      const response = await fetch(`${apiUrl}/api/contacts/${contactToEdit.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(contactToEdit),
      });

      if (response.ok) {
        const updatedContact = await response.json();
        setContacts(contacts.map(contact =>
          contact.id === updatedContact.id ? { ...updatedContact, tags: contact.tags } : contact
        ));
        setIsEditModalOpen(false);
        setContactToEdit(null);
        toast({
          title: 'Contact updated',
          description: 'Your contact has been updated successfully.',
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
      } else {
        const errorData = await response.json();
        toast({
          title: 'Error updating contact',
          description: errorData.detail || 'There was an error updating your contact.',
          status: 'error',
          duration: 3000,
          isClosable: true,
        });
      }
    } catch (error) {
      console.error('Error updating contact:', error);
      toast({
        title: 'Error',
        description: 'Failed to update contact',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    }
  };

  const handleDeleteContact = async (contactId: number) => {
    try {
      const response = await fetch(`${apiUrl}/api/contacts/${contactId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        setContacts(contacts.filter(contact => contact.id !== contactId));
        toast({
          title: 'Contact deleted',
          description: 'Your contact has been deleted successfully.',
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
      } else {
        toast({
          title: 'Error deleting contact',
          description: 'There was an error deleting your contact.',
          status: 'error',
          duration: 3000,
          isClosable: true,
        });
      }
    } catch (error) {
      console.error('Error deleting contact:', error);
      toast({
        title: 'Error',
        description: 'Failed to delete contact',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    }
  };

  const handleAddTagToContact = async (contactId: number, tagId: number) => {
    try {
      const response = await fetch(`${apiUrl}/api/tags/contact-tags/?contact_id=${contactId}&tag_id=${tagId}`, {
        method: 'POST',
      });

      if (response.ok) {
        // Update local state to reflect the new tag
        setContacts(contacts.map(contact => {
          if (contact.id === contactId) {
            const tagToAdd = tags.find(tag => tag.id === tagId);
            if (tagToAdd && !contact.tags.some(tag => tag.id === tagId)) {
              return {
                ...contact,
                tags: [...contact.tags, tagToAdd]
              };
            }
          }
          return contact;
        }));
        toast({
          title: 'Tag added',
          description: 'Tag has been added to the contact.',
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
      } else {
        const errorData = await response.json();
        toast({
          title: 'Error adding tag',
          description: errorData.detail || 'There was an error adding the tag.',
          status: 'error',
          duration: 3000,
          isClosable: true,
        });
      }
    } catch (error) {
      console.error('Error adding tag to contact:', error);
      toast({
        title: 'Error',
        description: 'Failed to add tag to contact',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    }
  };

  const handleRemoveTagFromContact = async (contactId: number, tagId: number) => {
    try {
      const response = await fetch(`${apiUrl}/api/tags/contact-tags/?contact_id=${contactId}&tag_id=${tagId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        // Update local state to reflect the removed tag
        setContacts(contacts.map(contact => {
          if (contact.id === contactId) {
            return {
              ...contact,
              tags: contact.tags.filter(tag => tag.id !== tagId)
            };
          }
          return contact;
        }));
        toast({
          title: 'Tag removed',
          description: 'Tag has been removed from the contact.',
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
      } else {
        const errorData = await response.json();
        toast({
          title: 'Error removing tag',
          description: errorData.detail || 'There was an error removing the tag.',
          status: 'error',
          duration: 3000,
          isClosable: true,
        });
      }
    } catch (error) {
      console.error('Error removing tag from contact:', error);
      toast({
        title: 'Error',
        description: 'Failed to remove tag from contact',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    }
  };

  const handleBulkImport = async (file: File, tagIds: number[]) => {
    const formData = new FormData();
    formData.append('file', file);
    if (tagIds.length > 0) {
      formData.append('tag_ids', tagIds.join(','));
    }

    try {
      const response = await fetch(`${apiUrl}/api/contacts/bulk-import/`, {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const result = await response.json();
        // Refresh contacts after successful import
        fetchContacts();
        return {
          success: true,
          message: result.message,
          errors: result.errors || []
        };
      } else {
        const errorData = await response.json();
        return {
          success: false,
          message: errorData.detail || 'Failed to import contacts'
        };
      }
    } catch (error) {
      console.error('Error importing contacts:', error);
      return {
        success: false,
        message: 'Failed to import contacts'
      };
    }
  };

  return (
    <Box p={6}>
      <HStack justify="space-between" mb={6}>
        <Heading size="lg">Contacts</Heading>
        <HStack>
          <Button
            onClick={() => setIsBulkImportModalOpen(true)}
          >
            Bulk Import
          </Button>
          <Button
            leftIcon={<AddIcon />}
            colorScheme="blue"
            onClick={() => setIsCreateModalOpen(true)}
          >
            Add Contact
          </Button>
        </HStack>
      </HStack>

      <SearchAndFilter
        searchTerm={searchTerm}
        selectedTagId={selectedTag}
        tags={tags}
        onSearchChange={setSearchTerm}
        onTagChange={setSelectedTag}
      />

      <ContactTable
        contacts={contacts}
        tags={tags}
        onEdit={(contact) => {
          setContactToEdit(contact);
          setIsEditModalOpen(true);
        }}
        onDelete={handleDeleteContact}
        onAddTag={handleAddTagToContact}
        onRemoveTag={handleRemoveTagFromContact}
      />

      {/* Create Contact Modal */}
      <Modal isOpen={isCreateModalOpen} onClose={() => setIsCreateModalOpen(false)}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Add New Contact</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <ContactForm
              formData={newContact}
              onChange={(field, value) => setNewContact({ ...newContact, [field]: value })}
            />
          </ModalBody>

          <ModalFooter>
            <Button variant="ghost" mr={3} onClick={() => setIsCreateModalOpen(false)}>
              Cancel
            </Button>
            <Button
              colorScheme="blue"
              onClick={handleCreateContact}
              isDisabled={!newContact.name || !newContact.email}
            >
              Create Contact
            </Button>
          </ModalFooter>
        </ModalContent>
      </Modal>

      {/* Edit Contact Modal */}
      <Modal isOpen={isEditModalOpen} onClose={() => setIsEditModalOpen(false)}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Edit Contact</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            {contactToEdit && (
              <ContactForm
                formData={{
                  name: contactToEdit.name,
                  email: contactToEdit.email,
                  phone: contactToEdit.phone || '',
                  company: contactToEdit.company || '',
                  position: contactToEdit.position || '',
                  notes: contactToEdit.notes || ''
                }}
                onChange={(field, value) => {
                  if (contactToEdit) {
                    setContactToEdit({ ...contactToEdit, [field]: value });
                  }
                }}
              />
            )}
          </ModalBody>

          <ModalFooter>
            <Button variant="ghost" mr={3} onClick={() => setIsEditModalOpen(false)}>
              Cancel
            </Button>
            <Button
              colorScheme="blue"
              onClick={handleUpdateContact}
              isDisabled={!contactToEdit?.name || !contactToEdit?.email}
            >
              Update Contact
            </Button>
          </ModalFooter>
        </ModalContent>
      </Modal>

      {/* Bulk Import Modal */}
      <BulkImportModal
        isOpen={isBulkImportModalOpen}
        onClose={() => setIsBulkImportModalOpen(false)}
        onImport={handleBulkImport}
        tags={tags}
      />
    </Box>
  );
};

export default ContactsPage;
</content>
</action>

