from fastapi import APIRouter, HTTPException, Depends, status, Query
from sqlalchemy.orm import Session
from typing import List, Optional
from datetime import datetime
from database import get_db, create_tables
from models.movie_model import Movie
from models.movie import MovieCreate, MovieUpdate, MovieResponse

# Create tables if they don't exist
create_tables()

router = APIRouter()

@router.post("/movies/", response_model=MovieResponse, status_code=status.HTTP_201_CREATED)
def create_movie(movie: MovieCreate, db: Session = Depends(get_db)):
    db_movie = Movie(
        title=movie.title,
        rating=movie.rating,
        review=movie.review,
        date_watched=datetime.now()
    )
    db.add(db_movie)
    db.commit()
    db.refresh(db_movie)
    return db_movie

@router.get("/movies/", response_model=List[MovieResponse])
def get_movies(
    rating: Optional[int] = Query(None, ge=1, le=5),
    search: Optional[str] = None,
    db: Session = Depends(get_db)
):
    query = db.query(Movie)

    if rating is not None:
        query = query.filter(Movie.rating == rating)

    if search:
        query = query.filter(Movie.title.contains(search))

    return query.order_by(Movie.date_watched.desc()).all()

@router.get("/movies/{movie_id}/", response_model=MovieResponse)
def get_movie(movie_id: int, db: Session = Depends(get_db)):
    db_movie = db.query(Movie).filter(Movie.id == movie_id).first()
    if not db_movie:
        raise HTTPException(status_code=404, detail="Movie not found")
    return db_movie

@router.put("/movies/{movie_id}/", response_model=MovieResponse)
def update_movie(movie_id: int, movie: MovieUpdate, db: Session = Depends(get_db)):
    db_movie = db.query(Movie).filter(Movie.id == movie_id).first()
    if not db_movie:
        raise HTTPException(status_code=404, detail="Movie not found")

    # Update only provided fields
    if movie.title is not None:
        db_movie.title = movie.title
    if movie.rating is not None:
        db_movie.rating = movie.rating
    if movie.review is not None:
        db_movie.review = movie.review

    db.commit()
    db.refresh(db_movie)
    return db_movie

@router.delete("/movies/{movie_id}/", status_code=status.HTTP_204_NO_CONTENT)
def delete_movie(movie_id: int, db: Session = Depends(get_db)):
    db_movie = db.query(Movie).filter(Movie.id == movie_id).first()
    if not db_movie:
        raise HTTPException(status_code=404, detail="Movie not found")

    db.delete(db_movie)
    db.commit()
    return
