<action type="file" path="frontend/src/components/TagManagement.tsx">
import React, { useState, useEffect } from 'react';
import {
  Box,
  Button,
  FormControl,
  FormLabel,
  Input,
  Modal,
  ModalBody,
  ModalCloseButton,
  ModalContent,
  ModalFooter,
  ModalHeader,
  ModalOverlay,
  useDisclosure,
  useToast,
  VStack,
  HStack,
  Tag,
  TagLabel,
  TagCloseButton,
  Wrap,
  WrapItem,
  Text,
  IconButton,
  useBreakpointValue,
} from '@chakra-ui/react';
import { AddIcon, EditIcon } from '@chakra-ui/icons';

interface TagType {
  id: number;
  name: string;
}

const API_URL = import.meta.env.VITE_API_URL;

const TagManagement: React.FC = () => {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const [tags, setTags] = useState<TagType[]>([]);
  const [newTagName, setNewTagName] = useState('');
  const [editingTag, setEditingTag] = useState<TagType | null>(null);
  const [loading, setLoading] = useState(false);
  const toast = useToast();
  const isMobile = useBreakpointValue({ base: true, md: false });

  // Fetch all tags
  const fetchTags = async () => {
    try {
      const response = await fetch(`${API_URL}/tags/`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data: TagType[] = await response.json();
      setTags(data);
    } catch (error: any) {
      toast({
        title: 'Error fetching tags',
        description: error.message,
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    }
  };

  useEffect(() => {
    fetchTags();
  }, []);

  const handleCreateTag = async () => {
    if (!newTagName.trim()) return;

    setLoading(true);
    try {
      const response = await fetch(`${API_URL}/tags/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: newTagName }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || `HTTP error! status: ${response.status}`);
      }

      const createdTag: TagType = await response.json();
      setTags([...tags, createdTag]);
      setNewTagName('');
      toast({
        title: 'Tag created',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (error: any) {
      toast({
        title: 'Error creating tag',
        description: error.message,
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteTag = async (tagId: number) => {
    if (!window.confirm('Are you sure you want to delete this tag?')) {
      return;
    }

    try {
      const response = await fetch(`${API_URL}/tags/${tagId}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      setTags(tags.filter(tag => tag.id !== tagId));
      toast({
        title: 'Tag deleted',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (error: any) {
      toast({
        title: 'Error deleting tag',
        description: error.message,
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    }
  };

  const openEditModal = (tag: TagType) => {
    setEditingTag(tag);
    setNewTagName(tag.name);
    onOpen();
  };

  const handleUpdateTag = async () => {
    if (!editingTag || !newTagName.trim()) return;

    setLoading(true);
    try {
      const response = await fetch(`${API_URL}/tags/${editingTag.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: newTagName }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || `HTTP error! status: ${response.status}`);
      }

      const updatedTag: TagType = await response.json();
      setTags(tags.map(tag => tag.id === updatedTag.id ? updatedTag : tag));
      setEditingTag(null);
      setNewTagName('');
      onClose();
      toast({
        title: 'Tag updated',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (error: any) {
      toast({
        title: 'Error updating tag',
        description: error.message,
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box>
      <VStack align="stretch" spacing={4}>
        <HStack>
          <Input
            value={newTagName}
            onChange={(e) => setNewTagName(e.target.value)}
            placeholder="Enter tag name"
            onKeyPress={(e) => e.key === 'Enter' && handleCreateTag()}
          />
          <Button
            onClick={editingTag ? handleUpdateTag : handleCreateTag}
            isLoading={loading}
            colorScheme="teal"
          >
            {editingTag ? 'Update' : 'Create'}
          </Button>
          {editingTag && (
            <Button
              onClick={() => {
                setEditingTag(null);
                setNewTagName('');
              }}
              variant="outline"
            >
              Cancel
            </Button>
          )}
        </HStack>

        <Wrap spacing={2}>
          {tags.map((tag) => (
            <WrapItem key={tag.id}>
              <Tag size="md" variant="subtle" colorScheme="teal">
                <TagLabel>{tag.name}</TagLabel>
                <IconButton
                  aria-label="Edit tag"
                  icon={<EditIcon />}
                  size="xs"
                  variant="ghost"
                  onClick={() => openEditModal(tag)}
                  ml={2}
                />
                <TagCloseButton onClick={() => handleDeleteTag(tag.id)} />
              </Tag>
            </WrapItem>
          ))}
        </Wrap>
      </VStack>

      {/* Edit Tag Modal */}
      <Modal isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Edit Tag</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <FormControl>
              <FormLabel>Tag Name</FormLabel>
              <Input
                value={newTagName}
                onChange={(e) => setNewTagName(e.target.value)}
                placeholder="Enter tag name"
              />
            </FormControl>
          </ModalBody>
          <ModalFooter>
            <Button colorScheme="blue" mr={3} onClick={handleUpdateTag} isLoading={loading}>
              Update
            </Button>
            <Button variant="ghost" onClick={onClose}>Cancel</Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </Box>
  );
};

export default TagManagement;
</action>

