from fastapi import APIRouter, HTTPException, Depends, status
from sqlalchemy.orm import Session
from typing import List
import hashlib
import secrets

from models.database_models import User
from models.user import UserCreate, UserLogin, UserResponse, UserUpdate
from database import get_db, create_tables

# Create tables if they don't exist
create_tables()

router = APIRouter()

def get_user_by_username(db: Session, username: str) -> User:
    return db.query(User).filter(User.username == username).first()

def create_user(db: Session, user: UserCreate) -> User:
    # Check if user already exists
    existing_user = get_user_by_username(db, user.username)
    if existing_user:
        raise HTTPException(
            status_code=status.HTTP_400_BAD_REQUEST,
            detail="Username already registered"
        )

    # Create new user
    db_user = User(
        username=user.username,
        coins=0,
        todos_completed=0
    )
    db.add(db_user)
    db.commit()
    db.refresh(db_user)
    return db_user

def update_user_coins(db: Session, user_id: int, coins: int) -> User:
    user = db.query(User).filter(User.id == user_id).first()
    if not user:
        raise HTTPException(
            status_code=status.HTTP_404_NOT_FOUND,
            detail="User not found"
        )

    user.coins += coins
    if coins > 0:
        user.todos_completed += 1

    db.commit()
    db.refresh(user)
    return user

def get_leaderboard(db: Session, limit: int = 10) -> List[User]:
    return db.query(User).order_by(User.coins.desc()).limit(limit).all()

@router.post("/users/", response_model=UserResponse, status_code=status.HTTP_201_CREATED)
def register_user(user: UserCreate, db: Session = Depends(get_db)):
    db_user = create_user(db, user)
    return db_user

@router.post("/users/login", response_model=UserResponse)
def login_user(user: UserLogin, db: Session = Depends(get_db)):
    db_user = get_user_by_username(db, user.username)
    if not db_user:
        # Create user if they don't exist (simplified login)
        db_user = create_user(db, user)
    return db_user

@router.get("/users/{user_id}", response_model=UserResponse)
def get_user(user_id: int, db: Session = Depends(get_db)):
    user = db.query(User).filter(User.id == user_id).first()
    if not user:
        raise HTTPException(
            status_code=status.HTTP_404_NOT_FOUND,
            detail="User not found"
        )
    return user

@router.put("/users/{user_id}", response_model=UserResponse)
def update_user(user_id: int, user_update: UserUpdate, db: Session = Depends(get_db)):
    user = db.query(User).filter(User.id == user_id).first()
    if not user:
        raise HTTPException(
            status_code=status.HTTP_404_NOT_FOUND,
            detail="User not found"
        )

    if user_update.username:
        # Check if new username is already taken
        existing_user = get_user_by_username(db, user_update.username)
        if existing_user and existing_user.id != user_id:
            raise HTTPException(
                status_code=status.HTTP_400_BAD_REQUEST,
                detail="Username already taken"
            )
        user.username = user_update.username

    db.commit()
    db.refresh(user)
    return user

@router.get("/leaderboard/", response_model=List[UserResponse])
def get_leaderboard_route(limit: int = 10, db: Session = Depends(get_db)):
    return get_leaderboard(db, limit)
