    return db.query(Todo).offset(skip).limit(limit).all()

def get_user_todos(db: Session, user_id: int) -> List[Todo]:
    return db.query(Todo).filter(
        (Todo.created_by_id == user_id) | (Todo.assigned_to_id == user_id)
    ).all()

def create_todo(db: Session, todo: TodoCreate, created_by_id: int) -> Todo:
    # Verify assigned user exists
    if todo.assigned_to:
        assigned_user = db.query(User).filter(User.username == todo.assigned_to).first()
        if not assigned_user:
            raise HTTPException(
                status_code=status.HTTP_400_BAD_REQUEST,
                detail="Assigned user not found"
            )
        assigned_to_id = assigned_user.id
    else:
        assigned_to_id = None

    # Create todo
    db_todo = Todo(
        title=todo.title,
        description=todo.description,
        created_by_id=created_by_id,
        assigned_to_id=assigned_to_id,
        coins_rewarded=10  # Default coins for completing a todo
    )
    db.add(db_todo)
    db.commit()
    db.refresh(db_todo)
    return db_todo

def update_todo(db: Session, todo_id: int, todo_update: TodoUpdate) -> Todo:
    db_todo = get_todo(db, todo_id)
    if not db_todo:
        raise HTTPException(
            status_code=status.HTTP_404_NOT_FOUND,
            detail="Todo not found"
        )

    # Update fields if provided
    if todo_update.title is not None:
        db_todo.title = todo_update.title
    if todo_update.description is not None:
        db_todo.description = todo_update.description
    if todo_update.status is not None:
        # If todo is being completed, award coins
        if todo_update.status == "completed" and db_todo.status != "completed":
            # Award coins to assigned user or creator if no assignee
            user_id = db_todo.assigned_to_id or db_todo.created_by_id
            update_user_coins(db, user_id, db_todo.coins_rewarded)
            db_todo.completed_at = datetime.now()
        db_todo.status = todo_update.status

    db.commit()
    db.refresh(db_todo)
    return db_todo

def delete_todo(db: Session, todo_id: int) -> bool:
    db_todo = get_todo(db, todo_id)
    if not db_todo:
        raise HTTPException(
            status_code=status.HTTP_404_NOT_FOUND,
            detail="Todo not found"
        )

    db.delete(db_todo)
    db.commit()
    return True

