<action type="file" filePath="frontend/src/services/todoService.ts">
const API_BASE = import.meta.env.VITE_API_URL || 'http://localhost:8002';

export interface Todo {
  id: number;
  title: string;
  description: string;
  status: 'pending' | 'in_progress' | 'completed';
  points: number;
  user_id: number;
  created_at: string;
  completed_at: string | null;
}

export interface User {
  id: number;
  username: string;
  coins: number;
  todos_completed: number;
  total_points: number;
}

export interface LeaderboardEntry {
  user_id: number;
  username: string;
  coins: number;
  todos_completed: number;
  total_points: number;
  rank: number;
}

export interface TodoCreate {
  title: string;
  description: string;
  points: number;
  user_id: number;
}

export interface TodoUpdate {
  title?: string;
  description?: string;
  status?: 'pending' | 'in_progress' | 'completed';
  points?: number;
}

export interface UserCreate {
  username: string;
}

// User API functions
export const getUsers = async (): Promise<User[]> => {
  const response = await fetch(`${API_BASE}/users/`);
  if (!response.ok) {
    throw new Error('Failed to fetch users');
  }
  return response.json();
};

export const getUser = async (userId: number): Promise<User> => {
  const response = await fetch(`${API_BASE}/users/${userId}`);
  if (!response.ok) {
    throw new Error('Failed to fetch user');
  }
  return response.json();
};

export const createUser = async (user: UserCreate): Promise<User> => {
  const response = await fetch(`${API_BASE}/users/`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(user),
  });
  if (!response.ok) {
    throw new Error('Failed to create user');
  }
  return response.json();
};

export const updateUser = async (userId: number, user: UserCreate): Promise<User> => {
  const response = await fetch(`${API_BASE}/users/${userId}`, {
    method: 'PUT',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(user),
  });
  if (!response.ok) {
    throw new Error('Failed to update user');
  }
  return response.json();
};

// Todo API functions
export const getTodos = async (): Promise<Todo[]> => {
  const response = await fetch(`${API_BASE}/todos/`);
  if (!response.ok) {
    throw new Error('Failed to fetch todos');
  }
  return response.json();
};

export const getTodo = async (todoId: number): Promise<Todo> => {
  const response = await fetch(`${API_BASE}/todos/${todoId}`);
  if (!response.ok) {
    throw new Error('Failed to fetch todo');
  }
  return response.json();
};

export const createTodo = async (todo: TodoCreate): Promise<Todo> => {
  const response = await fetch(`${API_BASE}/todos/`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(todo),
  });
  if (!response.ok) {
    throw new Error('Failed to create todo');
  }
  return response.json();
};

export const updateTodo = async (todoId: number, todo: TodoUpdate): Promise<Todo> => {
  const response = await fetch(`${API_BASE}/todos/${todoId}`, {
    method: 'PUT',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(todo),
  });
  if (!response.ok) {
    throw new Error('Failed to update todo');
  }
  return response.json();
};

export const deleteTodo = async (todoId: number): Promise<void> => {
  const response = await fetch(`${API_BASE}/todos/${todoId}`, {
    method: 'DELETE',
  });
  if (!response.ok) {
    throw new Error('Failed to delete todo');
  }
};

// Leaderboard API functions
export const getLeaderboard = async (): Promise<LeaderboardEntry[]> => {
  const response = await fetch(`${API_BASE}/leaderboard/`);
  if (!response.ok) {
    throw new Error('Failed to fetch leaderboard');
  }
  return response.json();
};
</action>

Now let's create the main TodoList page:

<action type="file" filePath="frontend/src/pages/TodoListPage.tsx">
import { useState, useEffect } from 'react';
import { Todo, User, TodoCreate, TodoUpdate } from '../services/todoService';
import { getTodos, createTodo, updateTodo, getUsers } from '../services/todoService';

export default function TodoListPage() {
  const [todos, setTodos] = useState<Todo[]>([]);
  const [users, setUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [newTodo, setNewTodo] = useState({
    title: '',
    description: '',
    points: 10,
    user_id: 1
  });
  const [selectedUser, setSelectedUser] = useState(1);

  // Fetch todos and users on component mount
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [todosData, usersData] = await Promise.all([
          getTodos(),
          getUsers()
        ]);
        setTodos(todosData);
        setUsers(usersData);
        if (usersData.length > 0) {
          setSelectedUser(usersData[0].id);
          setNewTodo(prev => ({ ...prev, user_id: usersData[0].id }));
        }
      } catch (err) {
        setError('Failed to fetch data');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleCreateTodo = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!newTodo.title.trim()) return;

    try {
      const todoData: TodoCreate = {
        ...newTodo,
        title: newTodo.title.trim(),
        description: newTodo.description.trim() || ''
      };

      const createdTodo = await createTodo(todoData);
      setTodos([...todos, createdTodo]);
      setNewTodo({
        title: '',
        description: '',
        points: 10,
        user_id: selectedUser
      });
    } catch (err) {
      setError('Failed to create todo');
      console.error(err);
    }
  };

  const handleToggleStatus = async (todo: Todo) => {
    try {
      let newStatus: 'pending' | 'in_progress' | 'completed' = 'pending';

      if (todo.status === 'pending') {
        newStatus = 'in_progress';
      } else if (todo.status === 'in_progress') {
        newStatus = 'completed';
      } else {
        newStatus = 'pending';
      }

      const updatedTodo = await updateTodo(todo.id, { status: newStatus });
      setTodos(todos.map(t => t.id === todo.id ? updatedTodo : t));
    } catch (err) {
      setError('Failed to update todo status');
      console.error(err);
    }
  };

  const getUserById = (userId: number) => {
    return users.find(user => user.id === userId) || { username: 'Unknown User' };
  };

  if (loading) {
    return (
      <div className="container">
        <div className="page-header">
          <h1 className="page-title">Todo List</h1>
          <p className="page-description">Loading your todos...</p>
        </div>
        <div className="text-center">
          <div className="loading">
            <div className="spinner"></div>
            <span>Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="page-header">
        <h1 className="page-title">Todo List</h1>
        <p className="page-description">Create and manage todos with your friends</p>
      </div>

      {error && (
        <div className="card mb-6">
          <div className="card-content">
            <div className="text-error">{error}</div>
          </div>
        </div>
      )}

      {/* Create Todo Form */}
      <div className="card mb-8">
        <div className="card-header">
          <h3 className="card-title">Create New Todo</h3>
          <p className="card-description">Add a new task to your list</p>
        </div>
        <div className="card-content">
          <form onSubmit={handleCreateTodo}>
            <div className="form-group">
              <label className="form-label" htmlFor="title">Title</label>
              <input
                type="text"
                id="title"
                className="form-input"
                value={newTodo.title}
                onChange={(e) => setNewTodo({ ...newTodo, title: e.target.value })}
                placeholder="What needs to be done?"
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label" htmlFor="description">Description</label>
              <textarea
                id="description"
                className="form-input form-textarea"
                value={newTodo.description}
                onChange={(e) => setNewTodo({ ...newTodo, description: e.target.value })}
                placeholder="Add details..."
              />
            </div>

            <div className="grid grid-cols-2">
              <div className="form-group">
                <label className="form-label" htmlFor="points">Points</label>
                <select
                  id="points"
                  className="form-input form-select"
                  value={newTodo.points}
                  onChange={(e) => setNewTodo({ ...newTodo, points: parseInt(e.target.value) })}
                >
                  <option value="5">5 points</option>
                  <option value="10">10 points</option>
                  <option value="15">15 points</option>
                  <option value="20">20 points</option>
                  <option value="25">25 points</option>
                  <option value="30">30 points</option>
                </select>
              </div>

              <div className="form-group">
                <label className="form-label" htmlFor="user">Assign to</label>
                <select
                  id="user"
                  className="form-input form-select"
                  value={selectedUser}
                  onChange={(e) => {
                    const userId = parseInt(e.target.value);
                    setSelectedUser(userId);
                    setNewTodo({ ...newTodo, user_id: userId });
                  }}
                >
                  {users.map(user => (
                    <option key={user.id} value={user.id}>
                      {user.username}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            <button type="submit" className="btn btn-primary">
              Add Todo
            </button>
          </form>
        </div>
      </div>

      {/* Todo List */}
      <div className="card">
        <div className="card-header">
          <h3 className="card-title">Your Todos</h3>
          <p className="card-description">Manage your tasks and track progress</p>
        </div>
        <div className="card-content">
          {todos.length === 0 ? (
            <div className="text-center py-8">
              <p className="text-secondary">No todos yet. Create your first todo above!</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 gap-4">
              {todos.map(todo => (
                <div 
                  key={todo.id} 
                  className={`card ${todo.status === 'completed' ? 'bg-gray-50' : ''}`}
                >
                  <div className="card-content">
                    <div className="flex justify-between items-start">
                      <div>
                        <h4 className={`font-semibold ${todo.status === 'completed' ? 'line-through text-secondary' : ''}`}>
                          {todo.title}
                        </h4>
                        {todo.description && (
                          <p className="text-secondary mt-2">{todo.description}</p>
                        )}
                        <div className="mt-3 flex items-center gap-4">
                          <span className="badge">
                            {todo.points} points
                          </span>
                          <span className="text-sm text-secondary">
                            Created by: {getUserById(todo.user_id).username}
                          </span>
                        </div>
                      </div>
                      <div className="flex flex-col items-end gap-2">
                        <span className={`badge ${
                          todo.status === 'pending' ? '' : 
                          todo.status === 'in_progress' ? 'badge-primary' : 'badge-success'
                        }`}>
                          {todo.status.replace('_', ' ')}
                        </span>
                        <button 
                          className={`btn btn-sm ${
                            todo.status === 'completed' ? 'btn-secondary' : 'btn-primary'
                          }`}
                          onClick={() => handleToggleStatus(todo)}
                        >
                          {todo.status === 'pending' ? 'Start' : 
                           todo.status === 'in_progress' ? 'Complete' : 'Reset'}
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
</action>

