<action type="update_file" path="frontend/src/pages/ProfilePage.tsx">
import { useState, useEffect } from 'react';
import { User, UserCreate } from '../services/todoService';
import { getUsers, createUser, updateUser } from '../services/todoService';

export default function ProfilePage() {
  const [users, setUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [newUser, setNewUser] = useState({ username: '' });
  const [editingUser, setEditingUser] = useState<User | null>(null);
  const [editForm, setEditForm] = useState({ username: '' });

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        setLoading(true);
        const data = await getUsers();
        setUsers(data);
      } catch (err) {
        setError('Failed to fetch users');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);

  const handleCreateUser = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!newUser.username.trim()) return;

    try {
      const userData: UserCreate = {
        username: newUser.username.trim()
      };

      const createdUser = await createUser(userData);
      setUsers([...users, createdUser]);
      setNewUser({ username: '' });
    } catch (err) {
      setError('Failed to create user');
      console.error(err);
    }
  };

  const handleStartEdit = (user: User) => {
    setEditingUser(user);
    setEditForm({ username: user.username });
  };

  const handleUpdateUser = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingUser) return;

    try {
      const userData: UserCreate = {
        username: editForm.username.trim()
      };

      const updatedUser = await updateUser(editingUser.id, userData);
      setUsers(users.map(u => u.id === updatedUser.id ? updatedUser : u));
      setEditingUser(null);
    } catch (err) {
      setError('Failed to update user');
      console.error(err);
    }
  };

  const handleCancelEdit = () => {
    setEditingUser(null);
  };

  if (loading) {
    return (
      <div className="container">
        <div className="page-header">
          <h1 className="page-title">User Management</h1>
          <p className="page-description">Manage users for your todo app</p>
        </div>
        <div className="text-center">
          <div className="loading">
            <div className="spinner"></div>
            <span>Loading users...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="page-header">
        <h1 className="page-title">User Management</h1>
        <p className="page-description">Manage users for your todo app</p>
      </div>

      {error && (
        <div className="card mb-6">
          <div className="card-content">
            <div className="text-error">{error}</div>
          </div>
        </div>
      )}

      {/* Create User Form */}
      <div className="card mb-8">
        <div className="card-header">
          <h3 className="card-title">Create New User</h3>
          <p className="card-description">Add a new user to your todo app</p>
        </div>
        <div className="card-content">
          <form onSubmit={handleCreateUser}>
            <div className="form-group">
              <label className="form-label" htmlFor="username">Username</label>
              <input
                type="text"
                id="username"
                className="form-input"
                value={newUser.username}
                onChange={(e) => setNewUser({ username: e.target.value })}
                placeholder="Enter username"
                required
              />
            </div>
            <button type="submit" className="btn btn-primary">
              Add User
            </button>
          </form>
        </div>
      </div>

      {/* Users List */}
      <div className="card">
        <div className="card-header">
          <h3 className="card-title">Users</h3>
          <p className="card-description">Manage existing users</p>
        </div>
        <div className="card-content">
          {users.length === 0 ? (
            <div className="text-center py-8">
              <p className="text-secondary">No users yet. Create your first user above!</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 gap-4">
              {users.map(user => (
                <div key={user.id} className="card">
                  {editingUser?.id === user.id ? (
                    // Edit form
                    <div className="card-content">
                      <form onSubmit={handleUpdateUser}>
                        <div className="form-group">
                          <label className="form-label" htmlFor={`edit-username-${user.id}`}>Username</label>
                          <input
                            type="text"
                            id={`edit-username-${user.id}`}
                            className="form-input"
                            value={editForm.username}
                            onChange={(e) => setEditForm({ username: e.target.value })}
                            required
                          />
                        </div>
                        <div className="flex gap-2">
                          <button type="submit" className="btn btn-primary btn-sm">
                            Save
                          </button>
                          <button 
                            type="button" 
                            className="btn btn-secondary btn-sm"
                            onClick={handleCancelEdit}
                          >
                            Cancel
                          </button>
                        </div>
                      </form>
                    </div>
                  ) : (
                    // User display
                    <div className="card-content">
                      <div className="flex justify-between items-center">
                        <div className="flex items-center gap-4">
                          <div className="avatar">
                            <div className="avatar-fallback">
                              {user.username.charAt(0).toUpperCase()}
                            </div>
                          </div>
                          <div>
                            <h4 className="font-semibold">{user.username}</h4>
                            <div className="flex gap-4 mt-2">
                              <div className="text-center">
                                <div className="font-bold">{user.total_points}</div>
                                <div className="text-sm text-secondary">Points</div>
                              </div>
                              <div className="text-center">
                                <div className="font-bold">{user.coins}</div>
                                <div className="text-sm text-secondary">Coins</div>
                              </div>
                              <div className="text-center">
                                <div className="font-bold">{user.todos_completed}</div>
                                <div className="text-sm text-secondary">Completed</div>
                              </div>
                            </div>
                          </div>
                        </div>
                        <button 
                          className="btn btn-outline btn-sm"
                          onClick={() => handleStartEdit(user)}
                        >
                          Edit
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
</action>

