  background-color: var(--color-gray-100);
  color: var(--color-gray-700);
  border-color: var(--color-gray-300);
}

.todo-status-in-progress {
  background-color: var(--color-primary);
  color: var(--color-white);
  border-color: var(--color-primary);
}

.todo-status-completed {
  background-color: var(--color-success);
  color: var(--color-white);
  border-color: var(--color-success);
}

.leaderboard-card-first {
  border: 2px solid var(--color-primary);
  box-shadow: var(--shadow-md);
  transform: scale(1.02);
}

.leaderboard-card-second {
  border: 1px solid var(--color-gray-300);
  box-shadow: var(--shadow-sm);
}

.leaderboard-card-third {
  border: 1px solid var(--color-amber-600);
  box-shadow: var(--shadow-sm);
}

.leaderboard-rank-first {
  background-color: var(--color-primary);
  color: var(--color-white);
  font-weight: bold;
}

.leaderboard-rank-second {
  background-color: var(--color-gray-300);
  color: var(--color-gray-800);
  font-weight: bold;
}

.leaderboard-rank-third {
  background-color: var(--color-amber-600);
  color: var(--color-white);
  font-weight: bold;
}

.todo-card-completed {
  background-color: var(--color-gray-50);
  opacity: 0.8;
}

.points-badge {
  background: linear-gradient(135deg, var(--color-primary), var(--color-primary-light));
  color: var(--color-white);
  font-weight: 600;
  padding: var(--spacing-1) var(--spacing-3);
  border-radius: var(--radius);
  font-size: 0.875rem;
}

.coins-badge {
  background: linear-gradient(135deg, var(--color-amber-500), var(--color-amber-600));
  color: var(--color-white);
  font-weight: 600;
  padding: var(--spacing-1) var(--spacing-3);
  border-radius: var(--radius);
  font-size: 0.875rem;
}

/* Animation for completed todos */
@keyframes fadeIn {
  from { opacity: 0; transform: translateY(10px); }
  to { opacity: 1; transform: translateY(0); }
}

.todo-item {
  animation: fadeIn 0.3s ease-out;
}

/* Responsive adjustments */
@media (max-width: 768px) {
  .leaderboard-card {
    flex-direction: column;
  }

  .leaderboard-user-info {
    text-align: center;
  }

  .leaderboard-stats {
    justify-content: center;
    margin-top: var(--spacing-3);
  }
}
