  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [newTodo, setNewTodo] = useState({
    title: '',
    description: '',
    points: 10,
    user_id: 1
  });
  const [selectedUser, setSelectedUser] = useState(1);

  // Fetch todos and users on component mount
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [todosData, usersData] = await Promise.all([
          getTodos(),
          getUsers()
        ]);
        setTodos(todosData);
        setUsers(usersData);
        if (usersData.length > 0) {
          setSelectedUser(usersData[0].id);
          setNewTodo(prev => ({ ...prev, user_id: usersData[0].id }));
        }
      } catch (err) {
        setError('Failed to fetch data');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleCreateTodo = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!newTodo.title.trim()) return;

    try {
      const todoData: TodoCreate = {
        ...newTodo,
        title: newTodo.title.trim(),
        description: newTodo.description.trim() || ''
      };

      const createdTodo = await createTodo(todoData);
      setTodos([...todos, createdTodo]);
      setNewTodo({
        title: '',
        description: '',
        points: 10,
        user_id: selectedUser
      });
    } catch (err) {
      setError('Failed to create todo');
      console.error(err);
    }
  };

  const handleToggleStatus = async (todo: Todo) => {
    try {
      let newStatus: 'pending' | 'in_progress' | 'completed' = 'pending';

      if (todo.status === 'pending') {
        newStatus = 'in_progress';
      } else if (todo.status === 'in_progress') {
        newStatus = 'completed';
      } else {
        newStatus = 'pending';
      }

      const updatedTodo = await updateTodo(todo.id, { status: newStatus });
      setTodos(todos.map(t => t.id === todo.id ? updatedTodo : t));
    } catch (err) {
      setError('Failed to update todo status');
      console.error(err);
    }
  };

  const getUserById = (userId: number) => {
    return users.find(user => user.id === userId) || { username: 'Unknown User' };
  };

  const getStatusClass = (status: string) => {
    switch (status) {
      case 'pending':
        return 'todo-status-pending';
      case 'in_progress':
        return 'todo-status-in-progress';
      case 'completed':
        return 'todo-status-completed';
      default:
        return '';
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="page-header">
          <h1 className="page-title">Todo List</h1>
          <p className="page-description">Loading your todos...</p>
        </div>
        <div className="text-center">
          <div className="loading">
            <div className="spinner"></div>
            <span>Loading...</span>
          </div>
        </div>
      </div>
    );
