import React, { useState, useEffect } from 'react';
import { TodoItem } from '../components/TodoItem';
import { Leaderboard } from '../components/Leaderboard';
import { UserStats } from '../components/UserStats';

interface Todo {
  id: number;
  title: string;
  description: string;
  completed: boolean;
  created_at: string;
  completed_at: string | null;
  owner_id: number;
}

interface User {
  id: number;
  username: string;
  email: string;
  coins: number;
  created_at: string;
}

const TodoPage: React.FC = () => {
  const [todos, setTodos] = useState<Todo[]>([]);
  const [newTodo, setNewTodo] = useState({ title: '', description: '' });
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Mock user data for now - in a real app, this would come from authentication
  const mockUser: User = {
    id: 1,
    username: 'alice',
    email: 'alice@example.com',
    coins: 50,
    created_at: '2025-08-11T18:02:32'
  };

  // Mock todo data for now - in a real app, this would come from the API
  const mockTodos: Todo[] = [
    {
      id: 1,
      title: 'Create a todo app',
      description: 'Build a collaborative todo app with leaderboards',
      completed: true,
      created_at: '2025-08-11T18:02:32',
      completed_at: '2025-08-11T19:02:32',
      owner_id: 1
    },
    {
      id: 2,
      title: 'Add coin system',
      description: 'Implement a coin reward system for completed todos',
      completed: false,
      created_at: '2025-08-11T19:02:32',
      completed_at: null,
      owner_id: 1
    }
  ];

  useEffect(() => {
    // Simulate API call
    setTimeout(() => {
      setUser(mockUser);
      setTodos(mockTodos);
      setLoading(false);
    }, 500);
  }, []);

  const handleCreateTodo = () => {
    if (!newTodo.title.trim()) return;

    const todo: Todo = {
      id: todos.length + 1,
      title: newTodo.title,
      description: newTodo.description,
      completed: false,
      created_at: new Date().toISOString(),
      completed_at: null,
      owner_id: user?.id || 1
    };

    setTodos([...todos, todo]);
    setNewTodo({ title: '', description: '' });
  };

  const handleToggleTodo = (id: number) => {
    setTodos(todos.map(todo => 
      todo.id === id ? { ...todo, completed: !todo.completed, completed_at: !todo.completed ? new Date().toISOString() : null } : todo
    ));
  };

  const handleDeleteTodo = (id: number) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  if (loading) {
    return <div className="page-header"><h1 className="page-title">Loading...</h1></div>;
  }

  return (
    <div>
      <div className="page-header">
        <h1 className="page-title">Todo Dashboard</h1>
        <p className="page-description">
          Manage your todos and track your progress on the leaderboard
        </p>
      </div>

      <div className="grid grid-cols-3 gap-6">
        {/* Main Todo List */}
        <div className="col-span-2">
          <div className="card">
            <div className="card-header">
              <h3 className="card-title">Your Todos</h3>
              <p className="card-description">Create and manage your todo items</p>
            </div>
            <div className="card-content">
              {/* Add Todo Form */}
              <div className="form-group mb-6">
                <input
                  type="text"
                  className="form-input mb-3"
                  placeholder="What needs to be done?"
                  value={newTodo.title}
                  onChange={(e) => setNewTodo({...newTodo, title: e.target.value})}
                />
                <textarea
                  className="form-input mb-3"
                  placeholder="Add a description (optional)"
                  value={newTodo.description}
                  onChange={(e) => setNewTodo({...newTodo, description: e.target.value})}
                />
                <button className="btn btn-primary" onClick={handleCreateTodo}>
                  Add Todo
                </button>
              </div>

              {/* Todo List */}
              <div>
                {todos.length === 0 ? (
                  <p className="text-center text-secondary">No todos yet. Add your first todo above!</p>
                ) : (
                  <ul className="space-y-3">
                    {todos.map(todo => (
                      <TodoItem
                        key={todo.id}
                        todo={todo}
                        onToggle={handleToggleTodo}
                        onDelete={handleDeleteTodo}
                      />
                    ))}
                  </ul>
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Sidebar with Stats and Leaderboard */}
        <div className="col-span-1">
          {/* User Stats */}
          <UserStats user={user} todos={todos} />

          {/* Leaderboard */}
          <Leaderboard />
        </div>
      </div>
    </div>
  );
};

export default TodoPage;
</action>

