import React, { useState, useEffect } from 'react';
import { TodoItem } from '../components/TodoItem';
import { Leaderboard } from '../components/Leaderboard';
import { UserStats } from '../components/UserStats';
import * as api from '../services/api';

interface Todo {
  id: number;
  title: string;
  description: string;
  completed: boolean;
  created_at: string;
  completed_at: string | null;
  owner_id: number;
}

interface User {
  id: number;
  username: string;
  email: string;
  coins: number;
  created_at: string;
}

interface LeaderboardEntry {
  user_id: number;
  username: string;
  coins: number;
  todos_completed: number;
}

const TodoPage: React.FC = () => {
  const [todos, setTodos] = useState<Todo[]>([]);
  const [newTodo, setNewTodo] = useState({ title: '', description: '' });
  const [user, setUser] = useState<User | null>(null);
  const [leaderboard, setLeaderboard] = useState<LeaderboardEntry[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // For demo purposes, we'll use a fixed user ID
  // In a real app, this would come from authentication
  const userId = 2; // This should match an existing user in your database

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch user data
        const userData = await api.getUser(userId);
        setUser(userData);

        // Fetch todos
        const todosData = await api.getTodos();
        // Filter todos for this user
        const userTodos = todosData.filter(todo => todo.owner_id === userId);
        setTodos(userTodos);

        // Fetch leaderboard
        const leaderboardData = await api.getLeaderboard();
        setLeaderboard(leaderboardData);

        setLoading(false);
      } catch (err) {
        console.error('Error fetching data:', err);
        setError('Failed to load data. Please try again.');
        setLoading(false);
      }
    };

    fetchData();
  }, [userId]);

  const handleCreateTodo = async () => {
    if (!newTodo.title.trim()) return;

    try {
      const todo = await api.createTodo(newTodo.title, newTodo.description, userId);
      setTodos([...todos, todo]);
      setNewTodo({ title: '', description: '' });
    } catch (err) {
      console.error('Error creating todo:', err);
      setError('Failed to create todo. Please try again.');
    }
  };

  const handleToggleTodo = async (id: number) => {
    try {
      const todo = todos.find(t => t.id === id);
      if (!todo) return;

      const updatedTodo = await api.updateTodo(id, { completed: !todo.completed });
      setTodos(todos.map(t => t.id === id ? updatedTodo : t));
    } catch (err) {
      console.error('Error updating todo:', err);
      setError('Failed to update todo. Please try again.');
    }
  };

  const handleDeleteTodo = async (id: number) => {
    try {
      await api.deleteTodo(id);
      setTodos(todos.filter(todo => todo.id !== id));
    } catch (err) {
      console.error('Error deleting todo:', err);
      setError('Failed to delete todo. Please try again.');
    }
  };

  if (loading) {
    return <div className="page-header"><h1 className="page-title">Loading...</h1></div>;
  }

  if (error) {
    return (
      <div className="page-header">
        <h1 className="page-title">Error</h1>
        <p className="page-description">{error}</p>
      </div>
    );
  }

  return (
    <div>
      <div className="page-header">
        <h1 className="page-title">Todo Dashboard</h1>
        <p className="page-description">
          Manage your todos and track your progress on the leaderboard
        </p>
      </div>

      <div className="grid grid-cols-3 gap-6">
        {/* Main Todo List */}
        <div className="col-span-2">
          <div className="card">
            <div className="card-header">
              <h3 className="card-title">Your Todos</h3>
              <p className="card-description">Create and manage your todo items</p>
            </div>
            <div className="card-content">
              {/* Add Todo Form */}
              <div className="form-group mb-6">
                <input
                  type="text"
                  className="form-input mb-3"
                  placeholder="What needs to be done?"
                  value={newTodo.title}
                  onChange={(e) => setNewTodo({...newTodo, title: e.target.value})}
                />
                <textarea
                  className="form-input mb-3"
                  placeholder="Add a description (optional)"
                  value={newTodo.description}
                  onChange={(e) => setNewTodo({...newTodo, description: e.target.value})}
                />
                <button className="btn btn-primary" onClick={handleCreateTodo}>
                  Add Todo
                </button>
              </div>

              {/* Todo List */}
              <div>
                {todos.length === 0 ? (
                  <p className="text-center text-secondary">No todos yet. Add your first todo above!</p>
                ) : (
                  <ul className="space-y-3">
                    {todos.map(todo => (
                      <TodoItem
                        key={todo.id}
                        todo={todo}
                        onToggle={handleToggleTodo}
                        onDelete={handleDeleteTodo}
                      />
                    ))}
                  </ul>
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Sidebar with Stats and Leaderboard */}
        <div className="col-span-1">
          {/* User Stats */}
          {user && <UserStats user={user} todos={todos} />}

          {/* Leaderboard */}
          <Leaderboard leaderboardData={leaderboard} />
        </div>
      </div>
    </div>
  );
};

export default TodoPage;
