from fastapi import APIRouter, Depends, HTTPException, status
from sqlalchemy.orm import Session
from typing import List
from database import get_db
from database.models import Todo
from models.todo_models import TodoCreate, TodoUpdate, TodoResponse

router = APIRouter()

@router.post("/", response_model=TodoResponse, status_code=status.HTTP_201_CREATED)
def create_todo(todo: TodoCreate, db: Session = Depends(get_db)):
    print(f"[Todo Service] Creating new todo: {todo.title}")
    try:
        db_todo = Todo(**todo.dict())
        db.add(db_todo)
        db.commit()
        db.refresh(db_todo)
        print(f"[Todo Service] Todo created successfully with ID: {db_todo.id}")
        return db_todo
    except Exception as e:
        db.rollback()
        print(f"[Todo Service] Error creating todo: {str(e)}")
        raise HTTPException(status_code=500, detail="Failed to create todo")

@router.get("/", response_model=List[TodoResponse])
def list_todos(skip: int = 0, limit: int = 100, db: Session = Depends(get_db)):
    print(f"[Todo Service] Fetching todos (skip={skip}, limit={limit})")
    try:
        todos = db.query(Todo).offset(skip).limit(limit).all()
        print(f"[Todo Service] Found {len(todos)} todos")
        return todos
    except Exception as e:
        print(f"[Todo Service] Error fetching todos: {str(e)}")
        raise HTTPException(status_code=500, detail="Failed to fetch todos")

@router.get("/{todo_id}", response_model=TodoResponse)
def get_todo(todo_id: int, db: Session = Depends(get_db)):
    print(f"[Todo Service] Fetching todo with ID: {todo_id}")
    try:
        todo = db.query(Todo).filter(Todo.id == todo_id).first()
        if not todo:
            print(f"[Todo Service] Todo with ID {todo_id} not found")
            raise HTTPException(status_code=404, detail="Todo not found")
        print(f"[Todo Service] Todo found: {todo.title}")
        return todo
    except HTTPException:
        raise
    except Exception as e:
        print(f"[Todo Service] Error fetching todo: {str(e)}")
        raise HTTPException(status_code=500, detail="Failed to fetch todo")

@router.put("/{todo_id}", response_model=TodoResponse)
def update_todo(todo_id: int, todo: TodoUpdate, db: Session = Depends(get_db)):
    print(f"[Todo Service] Updating todo with ID: {todo_id}")
    try:
        db_todo = db.query(Todo).filter(Todo.id == todo_id).first()
        if not db_todo:
            print(f"[Todo Service] Todo with ID {todo_id} not found")
            raise HTTPException(status_code=404, detail="Todo not found")

        update_data = todo.dict(exclude_unset=True)
        for key, value in update_data.items():
            setattr(db_todo, key, value)

        db.commit()
        db.refresh(db_todo)
        print(f"[Todo Service] Todo updated successfully: {db_todo.title}")
        return db_todo
    except HTTPException:
        raise
    except Exception as e:
        db.rollback()
        print(f"[Todo Service] Error updating todo: {str(e)}")
        raise HTTPException(status_code=500, detail="Failed to update todo")

@router.delete("/{todo_id}", status_code=status.HTTP_204_NO_CONTENT)
def delete_todo(todo_id: int, db: Session = Depends(get_db)):
    print(f"[Todo Service] Deleting todo with ID: {todo_id}")
    try:
        db_todo = db.query(Todo).filter(Todo.id == todo_id).first()
        if not db_todo:
            print(f"[Todo Service] Todo with ID {todo_id} not found")
            raise HTTPException(status_code=404, detail="Todo not found")

        db.delete(db_todo)
        db.commit()
        print(f"[Todo Service] Todo deleted successfully")
        return
    except HTTPException:
        raise
    except Exception as e:
        db.rollback()
        print(f"[Todo Service] Error deleting todo: {str(e)}")
        raise HTTPException(status_code=500, detail="Failed to delete todo")
